  // üé© MAGIC FUNCTION: Auto-create tenant after successful payment
  async function handleTenantCreationAfterPayment(paymentObject: any) {
    try {
      if (!stripe) return;

      console.log('üé© Starting automatic tenant creation...');

      // Get customer info from Stripe
      const customer = await stripe.customers.retrieve(paymentObject.customer);

      if (customer.deleted || !customer.metadata) {
        console.log('‚ö†Ô∏è Customer not found or no metadata');
        return;
      }

      const metadata = customer.metadata;

      // Check if this customer needs tenant creation
      if (metadata.autoCreateTenant !== 'true' || metadata.tenantCreation !== 'pending') {
        console.log('üôÉ No tenant creation needed for this customer');
        return;
      }

      // Extract tenant data from metadata
      const tenantName = metadata.tenantName || metadata.companyName || 'New Tenant';
      const companyName = metadata.companyName || '';
      const contactEmail = (customer as any).email || '';
      const contactPhone = metadata.contactPhone || '';
      const firstName = metadata.firstName || '';
      const lastName = metadata.lastName || '';

      console.log(`üè¢ Creating tenant: ${tenantName}`);

      // Create the tenant
      const [newTenant] = await db
        .insert(tenants)
        .values({
          name: tenantName,
          description: `Tenant creat automat dupƒÉ plata Stripe pentru ${companyName}`,
          status: 'active',
          companyName,
          contactEmail,
          contactPhone,
          subscriptionPlan: 'professional'
        })
        .returning();

      // Generate secure credentials
      const adminUsername = `admin_${newTenant.id}`;
      const adminPassword = generateSecurePassword();
      const hashedPassword = await bcrypt.hash(adminPassword, 10);

      // Create admin user
      const adminUser = await storage.createUser({
        username: adminUsername,
        password: hashedPassword,
        email: contactEmail,
        firstName,
        lastName,
        role: 'admin',
        tenantId: newTenant.id,
        companyName,
        subscriptionStatus: 'active'
      });

      // Initialize order sequence
      await tenantStorage.initializeOrderSequence(newTenant.id);

      // Update Stripe customer metadata
      await stripe.customers.update(paymentObject.customer, {
        metadata: {
          ...metadata,
          tenantCreation: 'completed',
          tenantId: newTenant.id.toString(),
          adminUsername
        }
      });

      console.log(`‚úÖ Tenant creat cu succes: ${tenantName} (ID: ${newTenant.id})`);
      console.log(`üë§ Admin user: ${adminUsername}`);

      // üìß Send welcome email with credentials
      await sendWelcomeEmailWithCredentials({
        tenantName,
        companyName,
        contactEmail,
        adminUsername,
        adminPassword,
        tenantId: newTenant.id,
        firstName,
        lastName
      });

    } catch (error) {
      console.error('üî• Error creating tenant after payment:', error);
    }
  }

  // üîë Generate secure password
  function generateSecurePassword(length = 12) {
    const charset = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*';
    let password = '';
    for (let i = 0; i < length; i++) {
      password += charset.charAt(Math.floor(Math.random() * charset.length));
    }
    return password;
  }

