      if (!isValidPassword) {
        return res.status(401).json({ error: 'Username sau parolƒÉ incorectƒÉ' });
      }

      // Store user session
      req.session.userId = user.id;
      req.session.tenantId = user.tenantId;

      console.log(`üîê Login reu»ôit pentru tenant ${tenantId}: ${username}`);

      res.json({
