import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { useToast } from '@/hooks/use-toast';
import { 
  Users, 
  DollarSign, 
  Settings, 
  Shield, 
  Database,
  Calendar,
  TrendingUp,
  Search,
  Filter,
  Download,
  Plus,
  Eye,
  Edit,
  Trash2,
  Building
} from 'lucide-react';
import { Link } from 'wouter';
import { useQuery } from '@tanstack/react-query';

interface User {
  id: number;
  username: string;
  email: string;
  role: string;
  companyName?: string;
  subscriptionStatus: string;
  createdAt: string;
  lastLoginAt?: string;
}

interface Analytics {
  totalSubscribers: number;
  activeSubscriptions: number;
  monthlyRevenue: number;
  trialUsers: number;
}

export default function AdminDashboard() {
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [isViewDialogOpen, setIsViewDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [editForm, setEditForm] = useState({
    username: '',
    email: '',
    companyName: '',
    subscriptionStatus: '',
    role: ''
  });
  const { toast } = useToast();

  // Fetch all subscribers
  const { data: subscribers = [], isLoading } = useQuery<User[]>({
    queryKey: ['/api/admin/subscribers'],
    retry: false,
  });

  // Fetch subscription analytics
  const { data: analytics = {} } = useQuery<Analytics>({
    queryKey: ['/api/admin/analytics'],
    retry: false,
  });

  const handleViewUser = (userId: number) => {
    const user = subscribers.find((u: User) => u.id === userId);
    if (user) {
      setSelectedUser(user);
      setIsViewDialogOpen(true);
    }
  };

  const handleEditUser = (userId: number) => {
    const user = subscribers.find((u: User) => u.id === userId);
    if (user) {
      setSelectedUser(user);
      setEditForm({
        username: user.username,
        email: user.email,
        companyName: user.companyName || '',
        subscriptionStatus: user.subscriptionStatus,
        role: user.role
      });
      setIsEditDialogOpen(true);
    }
  };

  const handleSaveEdit = async () => {
    if (selectedUser) {
      try {
        const response = await fetch(`/api/admin/users/${selectedUser.id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(editForm)
        });

        if (response.ok) {
          toast({
            title: "Utilizator actualizat",
            description: "ModificƒÉrile au fost salvate cu succes",
          });
          setIsEditDialogOpen(false);
          // Refresh data
          window.location.reload();
        } else {
          throw new Error('Failed to update user');
        }
      } catch (error) {
        toast({
          title: "Eroare",
          description: "Nu s-au putut salva modificƒÉrile",
          variant: "destructive",
        });
      }
    }
  };

  const handleDeleteUser = (userId: number) => {
    if (confirm('E»ôti sigur cƒÉ vrei sƒÉ »ôtergi acest utilizator?')) {
      fetch(`/api/admin/users/${userId}`, { method: 'DELETE' })
        .then(response => {
          if (response.ok) {
            toast({
              title: "Utilizator »ôters",
              description: "Utilizatorul a fost »ôters cu succes",
              variant: "destructive",
            });
            window.location.reload();
          }
        })
        .catch(() => {
          toast({
            title: "Eroare",
            description: "Nu s-a putut »ôterge utilizatorul",
            variant: "destructive",
          });
        });
    }
  };

  const handleAddSubscriber = () => {
    setEditForm({
      username: '',
      email: '',
      companyName: '',
      subscriptionStatus: 'active',
      role: 'subscriber'
    });
    setIsAddDialogOpen(true);
  };

  const handleSaveNewUser = async () => {
    try {
      const response = await fetch('/api/admin/users', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(editForm)
      });

      if (response.ok) {
        toast({
          title: "Utilizator adƒÉugat",
          description: "Noul utilizator a fost creat cu succes",
        });
        setIsAddDialogOpen(false);
        window.location.reload();
      } else {
        throw new Error('Failed to create user');
      }
    } catch (error) {
      toast({
        title: "Eroare",
        description: "Nu s-a putut crea utilizatorul",
        variant: "destructive",
      });
    }
  };

  const handleDatabaseAccess = (userId: number) => {
    toast({
      title: "Acces bazƒÉ de date",
      description: `Conectez la baza de date pentru utilizatorul ID: ${userId}`,
    });
    console.log('Database access for user:', userId);
  };

  const filteredSubscribers = subscribers.filter((user: User) => {
    const matchesSearch = user.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         user.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         user.companyName?.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesStatus = statusFilter === 'all' || user.subscriptionStatus === statusFilter;

    return matchesSearch && matchesStatus;
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-green-500/20 text-green-300 border-green-500/30';
      case 'trialing': return 'bg-blue-500/20 text-blue-300 border-blue-500/30';
      case 'canceled': return 'bg-red-500/20 text-red-300 border-red-500/30';
      default: return 'bg-gray-500/20 text-gray-300 border-gray-500/30';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'active': return 'Activ';
      case 'trialing': return 'Perioada probƒÉ';
      case 'canceled': return 'Anulat';
      case 'inactive': return 'Inactiv';
      default: return status;
    }
  };

