                <CompanyBalancesView />
              </motion.div>
            )}

            {/* Company Summary Tab */}
            {activeTab === 'summary' && (
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
