import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { useToast } from '@/hooks/use-toast';
import { Eye, EyeOff, Check, X, Loader2, CreditCard } from 'lucide-react';
import { Elements, CardNumberElement, CardExpiryElement, CardCvcElement, useStripe, useElements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import { Link } from 'wouter';

// Initialize Stripe - use LIVE key if available, otherwise test key
const stripePublicKey = import.meta.env.VITE_STRIPE_LIVE_PUBLIC_KEY || import.meta.env.VITE_STRIPE_PUBLIC_KEY;
const stripePromise = loadStripe(stripePublicKey!);

interface FormData {
  username: string;
  email: string;
  password: string;
  confirmPassword: string;
  firstName: string;
  lastName: string;
  companyName: string;
}

function RegisterForm() {
  const [formData, setFormData] = useState<FormData>({
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
    firstName: '',
    lastName: '',
    companyName: ''
  });

  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [usernameCheck, setUsernameCheck] = useState<{ available: boolean; message: string } | null>(null);
  const [emailCheck, setEmailCheck] = useState<{ available: boolean; message: string } | null>(null);
  const [isCheckingUsername, setIsCheckingUsername] = useState(false);
  const [isCheckingEmail, setIsCheckingEmail] = useState(false);
  const [clientSecret, setClientSecret] = useState<string>('');

  const { toast } = useToast();
  const stripe = useStripe();
  const elements = useElements();

  // Debounced username check
  useEffect(() => {
    if (formData.username.length >= 3) {
      setIsCheckingUsername(true);
      const timer = setTimeout(async () => {
        try {
          const response = await fetch('/api/auth/check-username', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ username: formData.username })
          });
          const result = await response.json();
          setUsernameCheck(result);
        } catch (error) {
          setUsernameCheck({ available: false, message: 'Eroare la verificare' });
        } finally {
          setIsCheckingUsername(false);
        }
      }, 500);
      return () => clearTimeout(timer);
    } else {
      setUsernameCheck(null);
    }
  }, [formData.username]);

  // Debounced email check
  useEffect(() => {
    if (formData.email.includes('@')) {
      setIsCheckingEmail(true);
      const timer = setTimeout(async () => {
        try {
          const response = await fetch('/api/auth/check-email', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ email: formData.email })
          });
          const result = await response.json();
          setEmailCheck(result);
        } catch (error) {
          setEmailCheck({ available: false, message: 'Eroare la verificare' });
        } finally {
          setIsCheckingEmail(false);
        }
      }, 500);
      return () => clearTimeout(timer);
    } else {
      setEmailCheck(null);
    }
  }, [formData.email]);

  const handleInputChange = (field: keyof FormData) => (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData(prev => ({ ...prev, [field]: e.target.value }));
  };

  const validateForm = () => {
    if (!formData.username || formData.username.length < 3) {
      toast({
        title: "Eroare",
        description: "Numele de utilizator trebuie sƒÉ aibƒÉ cel pu»õin 3 caractere",
        variant: "destructive"
      });
      return false;
    }

    if (!formData.email || !formData.email.includes('@')) {
      toast({
        title: "Eroare",
        description: "Te rog introdu o adresƒÉ de email validƒÉ",
        variant: "destructive"
      });
      return false;
    }

    if (!formData.password || formData.password.length < 6) {
      toast({
        title: "Eroare",
        description: "Parola trebuie sƒÉ aibƒÉ cel pu»õin 6 caractere",
        variant: "destructive"
      });
      return false;
    }

    if (formData.password !== formData.confirmPassword) {
      toast({
        title: "Eroare",
        description: "Parolele nu se potrivesc",
        variant: "destructive"
      });
      return false;
    }

    if (!formData.firstName || !formData.lastName || !formData.companyName) {
      toast({
        title: "Eroare",
        description: "Te rog completeazƒÉ toate c√¢mpurile obligatorii",
        variant: "destructive"
      });
      return false;
    }

    if (usernameCheck && !usernameCheck.available) {
      toast({
        title: "Eroare",
        description: "Numele de utilizator nu este disponibil",
        variant: "destructive"
      });
      return false;
    }

    if (emailCheck && !emailCheck.available) {
      toast({
        title: "Eroare",
        description: "Adresa de email nu este disponibilƒÉ",
        variant: "destructive"
      });
      return false;
    }

    // VALIDARE OBLIGATORIE PENTRU CARD
    if (!stripe || !elements) {
      toast({
        title: "Eroare",
        description: "Sistemul de platƒÉ nu este disponibil. Te rog re√ÆncarcƒÉ pagina.",
        variant: "destructive"
      });
      return false;
    }

    return true;
  };

  // Create Stripe setup intent when form is ready
  useEffect(() => {
    if (formData.username && formData.email && usernameCheck?.available && emailCheck?.available) {
      createSetupIntent();
    }
  }, [formData.username, formData.email, usernameCheck, emailCheck]);

  const createSetupIntent = async () => {
    try {
      const response = await fetch('/api/create-subscription', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ planId: 'professional', trialDays: 3 })
      });

      if (response.ok) {
        const { clientSecret } = await response.json();
        setClientSecret(clientSecret);
      }
    } catch (error) {
      console.log('Stripe setup will be handled later');
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) return;
    if (!stripe || !elements) return;

    setIsSubmitting(true);

    try {
      // PRIMUL: Validez cardul √Ænainte de orice altceva
      const cardNumberElement = elements.getElement(CardNumberElement);
      if (!cardNumberElement) {
        toast({
          title: "Eroare",
          description: "Te rog introdu detaliile cardului pentru a continua",
          variant: "destructive",
        });
        return;
      }

      // VERIFICARE COMPLETƒÇ STRIPE: Validez cardul prin √Æncercare de platƒÉ $0.50 + anulare
      toast({
        title: "Se verificƒÉ cardul...",
        description: "ValidƒÉm cardul cu Stripe (fƒÉrƒÉ taxare)",
        variant: "default",
      });

      const { error: cardError, paymentMethod } = await stripe.createPaymentMethod({
        type: 'card',
        card: cardNumberElement,
        billing_details: {
          name: `${formData.firstName} ${formData.lastName}`,
          email: formData.email,
        },
      });

      if (cardError) {
        toast({
          title: "Card invalid",
          description: cardError.message || "Te rog verificƒÉ detaliile cardului",
          variant: "destructive",
        });
        return;
      }

      if (!paymentMethod) {
        toast({
          title: "Eroare",
          description: "Te rog completeazƒÉ toate detaliile cardului",
          variant: "destructive",
        });
        return;
      }

      // TESTEZ CARDUL CU O PLATƒÇ MICƒÇ PENTRU VALIDARE REALƒÇ
      try {
        const testPaymentResponse = await fetch('/api/verify-card', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            paymentMethodId: paymentMethod.id,
            amount: 50, // $0.50 pentru test
            currency: 'usd'
          })
        });

        const testResult = await testPaymentResponse.json();

        if (!testPaymentResponse.ok) {
          throw new Error(testResult.error || 'Card verification failed');
        }

        // ANULEAZƒÇ IMEDIAT PLATA DE TEST
        if (testResult.paymentIntentId) {
          await fetch('/api/cancel-payment', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              paymentIntentId: testResult.paymentIntentId
            })
          });
        }

        toast({
          title: "‚úÖ Card validat cu succes!",
          description: "Cardul este valid »ôi poate fi folosit pentru plƒÉ»õi",
          variant: "default",
        });

      } catch (verificationError: any) {
        toast({
          title: "Card respins",
          description: verificationError.message || "Cardul nu poate fi folosit pentru plƒÉ»õi. Te rog √ÆncearcƒÉ alt card.",
          variant: "destructive",
        });
        return;
      }

      // AL DOILEA: Creez contul doar DUPƒÇ ce cardul este validat
      const userResponse = await fetch('/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          username: formData.username.trim(),
          email: formData.email.trim(),
          password: formData.password,
          firstName: formData.firstName.trim(),
          lastName: formData.lastName.trim(),
          companyName: formData.companyName.trim(),
          role: 'subscriber',
          subscriptionStatus: 'trialing',
          paymentMethodId: paymentMethod.id // Trimit »ôi ID-ul metodei de platƒÉ
        })
      });

      if (!userResponse.ok) {
        const errorData = await userResponse.json();
        throw new Error(errorData.error || 'Failed to create account');
      }

      // AL TREILEA: Setup subscription cu cardul validat (nu e necesar pentru trial)
      // Pentru perioada de trial, payment method-ul este deja ata»ôat la customer
      console.log('‚úÖ Trial setup complete - no immediate charge needed');

      toast({
        title: "üéâ Cont creat cu succes!",
        description: "Bun venit! Te redirec»õionez cƒÉtre pagina de login...",
        variant: "default"
      });

      // Clear form to prevent re-submission
      setFormData({
        username: '',
        email: '',
        password: '',
        confirmPassword: '',
        firstName: '',
        lastName: '',
        companyName: ''
      });

      // Redirect immediately
      setTimeout(() => {
        window.location.href = '/login';
      }, 1500);

    } catch (error: any) {
      let errorMessage = "A apƒÉrut o eroare. Te rog √ÆncearcƒÉ din nou.";

      if (error.message.includes('Username already exists')) {
        errorMessage = "Numele de utilizator este deja folosit. Te rog alege altul.";
        // Clear username to allow user to try again
        setFormData(prev => ({ ...prev, username: '' }));
        setUsernameCheck(null);
      } else if (error.message.includes('Email already exists')) {
        errorMessage = "Adresa de email este deja √ÆnregistratƒÉ. Te rog folose»ôte o altƒÉ adresƒÉ.";
        // Clear email to allow user to try again
        setFormData(prev => ({ ...prev, email: '' }));
        setEmailCheck(null);
      }

      toast({
        title: "Eroare la √Ænregistrare",
        description: errorMessage,
        variant: "destructive"
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-blue-800 to-indigo-900 flex items-center justify-center p-4">
      <Card className="w-full max-w-lg mx-auto bg-white/10 backdrop-blur-lg border-white/20 text-white">
        <CardHeader className="text-center">
          <CardTitle className="text-3xl font-bold mb-2">
            √énregistrare Transport Pro
          </CardTitle>
          <p className="text-gray-300">
            3 zile gratuit ‚Ä¢ ‚Ç¨99.99/lunƒÉ dupƒÉ trial
          </p>
        </CardHeader>

        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            {/* Username */}
            <div>
              <Label htmlFor="username" className="text-white">Nume utilizator *</Label>
              <div className="relative">
                <Input
                  id="username"
                  type="text"
                  value={formData.username}
                  onChange={handleInputChange('username')}
                  className="bg-white/10 border-white/20 text-white placeholder-gray-300"
                  placeholder="ex: fastexpress"
                  required
                />
                {isCheckingUsername && (
                  <Loader2 className="absolute right-3 top-1/2 transform -translate-y-1/2 w-4 h-4 animate-spin text-blue-400" />
                )}
                {!isCheckingUsername && usernameCheck && (
                  <div className="absolute right-3 top-1/2 transform -translate-y-1/2">
                    {usernameCheck.available ? (
                      <Check className="w-4 h-4 text-green-400" />
                    ) : (
                      <X className="w-4 h-4 text-red-400" />
                    )}
                  </div>
                )}
              </div>
              {usernameCheck && (
                <p className={`text-xs mt-1 ${usernameCheck.available ? 'text-green-400' : 'text-red-400'}`}>
                  {usernameCheck.message}
                </p>
              )}
            </div>

            {/* Email */}
            <div>
              <Label htmlFor="email" className="text-white">Email *</Label>
              <div className="relative">
                <Input
                  id="email"
                  type="email"
                  value={formData.email}
                  onChange={handleInputChange('email')}
                  className="bg-white/10 border-white/20 text-white placeholder-gray-300"
                  placeholder="ex: contact@fastexpress.ro"
                  required
                />
                {isCheckingEmail && (
                  <Loader2 className="absolute right-3 top-1/2 transform -translate-y-1/2 w-4 h-4 animate-spin text-blue-400" />
                )}
                {!isCheckingEmail && emailCheck && (
                  <div className="absolute right-3 top-1/2 transform -translate-y-1/2">
                    {emailCheck.available ? (
                      <Check className="w-4 h-4 text-green-400" />
                    ) : (
                      <X className="w-4 h-4 text-red-400" />
                    )}
                  </div>
                )}
              </div>
              {emailCheck && (
                <p className={`text-xs mt-1 ${emailCheck.available ? 'text-green-400' : 'text-red-400'}`}>
                  {emailCheck.message}
                </p>
              )}
            </div>

            {/* Password */}
            <div>
              <Label htmlFor="password" className="text-white">ParolƒÉ *</Label>
              <div className="relative">
                <Input
                  id="password"
                  type={showPassword ? "text" : "password"}
                  value={formData.password}
                  onChange={handleInputChange('password')}
                  className="bg-white/10 border-white/20 text-white placeholder-gray-300 pr-10"
                  placeholder="Minimum 6 caractere"
                  required
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-300 hover:text-white"
                >
                  {showPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                </button>
              </div>
            </div>

            {/* Confirm Password */}
            <div>
              <Label htmlFor="confirmPassword" className="text-white">ConfirmƒÉ parola *</Label>
              <div className="relative">
                <Input
                  id="confirmPassword"
                  type={showConfirmPassword ? "text" : "password"}
                  value={formData.confirmPassword}
                  onChange={handleInputChange('confirmPassword')}
                  className="bg-white/10 border-white/20 text-white placeholder-gray-300 pr-10"
                  placeholder="Reintroduce»õi parola"
                  required
                />
                <button
                  type="button"
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-300 hover:text-white"
                >
                  {showConfirmPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                </button>
              </div>
            </div>

            {/* First Name */}
            <div>
              <Label htmlFor="firstName" className="text-white">Prenume *</Label>
              <Input
                id="firstName"
                type="text"
                value={formData.firstName}
                onChange={handleInputChange('firstName')}
                className="bg-white/10 border-white/20 text-white placeholder-gray-300"
                placeholder="ex: Petrisor"
                required
              />
            </div>

            {/* Last Name */}
            <div>
              <Label htmlFor="lastName" className="text-white">Nume de familie *</Label>
              <Input
                id="lastName"
                type="text"
                value={formData.lastName}
                onChange={handleInputChange('lastName')}
                className="bg-white/10 border-white/20 text-white placeholder-gray-300"
                placeholder="ex: Popescu"
                required
              />
            </div>

            {/* Company Name */}
            <div>
              <Label htmlFor="companyName" className="text-white">Numele companiei *</Label>
              <Input
                id="companyName"
                type="text"
                value={formData.companyName}
                onChange={handleInputChange('companyName')}
                className="bg-white/10 border-white/20 text-white placeholder-gray-300"
                placeholder="ex: Fast Express SRL"
                required
              />
            </div>

            {/* Payment Information */}
            <div className="border-t border-white/20 pt-6">
              <div className="flex items-center space-x-2 mb-4">
                <CreditCard className="w-5 h-5 text-blue-400" />
                <Label className="text-white text-lg font-semibold">Detalii card (pentru trial)</Label>
              </div>
              <p className="text-gray-300 text-sm mb-4">
                <strong>Obligatoriu:</strong> Cardul este necesar pentru verificarea identitƒÉ»õii. Nu vei fi taxat √Æn perioada de probƒÉ de 3 zile.
              </p>

              {stripePublicKey?.startsWith('pk_test_') && (
                <div className="bg-amber-500/20 border border-amber-400/30 rounded-lg p-3 mb-4">
                  <p className="text-amber-200 text-xs">
                    <strong>MOD TEST:</strong> Folose»ôte carduri de test: 4242 4242 4242 4242, orice CVC (ex: 123), orice datƒÉ viitoare (ex: 12/30).
                  </p>
                </div>
              )}

              {stripePublicKey?.startsWith('pk_live_') && (
                <div className="bg-green-500/20 border border-green-400/30 rounded-lg p-3 mb-4">
                  <p className="text-green-200 text-xs">
                    <strong>MOD LIVE:</strong> Folose»ôte cardul tƒÉu real. PlƒÉ»õile vor fi procesate efectiv cu 99.99‚Ç¨/lunƒÉ dupƒÉ trial de 3 zile.
                  </p>
                </div>
              )}

              <div className="space-y-4">
                {/* Card Number */}
                <div>
                  <Label className="text-white text-sm">NumƒÉrul cardului *</Label>
                  <div className="bg-white/5 border border-white/20 rounded-lg p-4 mt-2">
                    <CardNumberElement
                      options={{
                        style: {
                          base: {
                            fontSize: '16px',
                            color: '#ffffff',
                            fontFamily: 'system-ui, -apple-system, sans-serif',
                            '::placeholder': {
                              color: '#9ca3af',
                            },
                            backgroundColor: 'transparent',
                          },
                          invalid: {
                            color: '#ef4444',
                          },
                        },
                        placeholder: '1234 1234 1234 1234',
                      }}
                    />
                  </div>
                </div>

                {/* Expiry and CVC */}
                <div className="grid grid-cols-2 gap-4">
                  <div>
