// Folose»ôte doar cheia PUBLICƒÇ √Æn frontend - NICIODATƒÇ cheia secretƒÉ!
const stripePublicKey = import.meta.env.VITE_STRIPE_PUBLIC_KEY || 'pk_test_demo';
console.log('Frontend Stripe key starts with:', stripePublicKey.substring(0, 7));

// VerificƒÉ cƒÉ este cheia publicƒÉ corectƒÉ »ôi creeazƒÉ stripePromise
const stripePromise = (() => {
  if (stripePublicKey.startsWith('sk_')) {
    console.error('‚ö†Ô∏è SECURITY ERROR: Secret key found in frontend! Using demo mode.');
    return null;
  } else if (stripePublicKey !== 'pk_test_demo') {
    return loadStripe(stripePublicKey);
  }
  return null;
})();

const planDetails = {
  professional: {
    name: 'Transport Pro',
    price: 99.99,
    description: 'Solu»õia completƒÉ pentru managementul transporturilor',
    trialDays: 3,
    popular: true,
    features: [
      'Comenzi nelimitate',
      'Tracking complet plƒÉ»õi »ôi comisioane',
      'Gestionare companii multiple',
      'Bilan»õuri automate »ôi rapoarte avansate',
      'Export PDF profesional cu logo',
      'Calculatoare comisioane avansate',
      'Istoric complet tranzac»õii',
      'Dashboard analiticƒÉ √Æn timp real',
      'Backup automat »ôi securitate',
      'Suport prioritar 24/7',
      'ActualizƒÉri gratuite',
      'Conformitate GDPR'
    ]
  }
};

function SubscribeForm({ planId }: { planId: string }) {
  const stripe = useStripe();
  const elements = useElements();
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);
  const [step, setStep] = useState<'info' | 'payment'>('info');

  // Tenant information state
  const [tenantInfo, setTenantInfo] = useState({
    companyName: '',
    firstName: '',
    lastName: '',
    contactEmail: '',
    contactPhone: '',
    tenantName: ''
  });

  const plan = planDetails[planId as keyof typeof planDetails];

  // Handle tenant info form submission
  const handleInfoSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!tenantInfo.companyName || !tenantInfo.contactEmail || !tenantInfo.firstName || !tenantInfo.lastName) {
      toast({
        title: "Date incomplete",
        description: "Completa»õi toate c√¢mpurile obligatorii.",
        variant: "destructive",
      });
      return;
    }

    // Set tenant name if not provided
    if (!tenantInfo.tenantName) {
      setTenantInfo(prev => ({ ...prev, tenantName: prev.companyName }));
    }

    setStep('payment');
  };

  const handlePaymentSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!stripe || !elements || !plan) {
      return;
    }

    setIsLoading(true);

    try {
      // Create subscription with tenant info
      const subscriptionResponse = await apiRequest('POST', '/api/create-subscription', {
        planId,
        trialDays: plan.trialDays,
        // Include tenant information
        ...tenantInfo
      });

      const subscriptionData = await subscriptionResponse.json();

      if (!subscriptionResponse.ok) {
        throw new Error(subscriptionData.message || 'Subscription failed');
      }

      // Submit elements first (required by Stripe)
      const submitResult = await elements.submit();
      if (submitResult.error) {
        throw new Error(submitResult.error.message);
      }

      // Now confirm the setup intent
      const { error, setupIntent } = await stripe.confirmSetup({
        elements,
        redirect: 'if_required',
        clientSecret: subscriptionData.clientSecret
      });

      if (error) {
        const errorMessage = error.message || "Eroare necunoscutƒÉ";
        if (errorMessage.includes("test mode") || errorMessage.includes("test card") || errorMessage.includes("declined")) {
          toast({
            title: "Card invalid pentru test",
            description: "Folose»ôte unul dintre cardurile de test Stripe afi»ôate mai sus",
            variant: "destructive",
          });
        } else {
          toast({
            title: "Eroare la configurarea abonamentului",
            description: errorMessage,
            variant: "destructive",
          });
        }
      } else if (setupIntent?.status === 'succeeded') {
        toast({
          title: "Perioada de probƒÉ activatƒÉ!",
          description: `${plan.trialDays} zile gratuite au √Ænceput`,
          variant: "default",
        });
        // Redirec»õionare manualƒÉ dupƒÉ succes
        setTimeout(() => {
          window.location.href = '/subscription-success';
        }, 1500);
      }
    } catch (err: any) {
      console.error("Setup error:", err);
      toast({
        title: "Eroare",
        description: err?.message || "A apƒÉrut o problemƒÉ la procesarea abonamentului",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  if (!plan) {
    return (
      <div className="text-center py-12">
        <p className="text-red-400">Plan invalid</p>
        <Link href="/pricing">
          <Button variant="outline" className="mt-4">
