import { motion, AnimatePresence } from "framer-motion";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { AlertCircle, UserPlus, X } from "lucide-react";
import { useState, useEffect } from "react";
import { useLocation } from 'wouter';

interface PendingMapping {
  driverName: string;
  suggestedCompany: string;
  alternatives: string[];
}

interface Company {
  id: number;
  name: string;
  commissionRate: string;
}

interface PendingDriverMappingsProps {
  pendingMappings: PendingMapping[];
  setPendingMappings: React.Dispatch<React.SetStateAction<PendingMapping[]>>;
  addDriverToDatabase: (driverName: string, selectedCompany: string) => Promise<string | null>;
  onMappingComplete: () => void;
}

export function PendingDriverMappings({
  pendingMappings,
  setPendingMappings,
  addDriverToDatabase,
  onMappingComplete
}: PendingDriverMappingsProps) {
  const { toast } = useToast();
  const [location] = useLocation();
  const [selectedCompanies, setSelectedCompanies] = useState<Record<string, string>>({});
  const [allCompanies, setAllCompanies] = useState<Company[]>([]);

  // Helper function to get correct API base URL based on context
  const getApiBaseUrl = () => {
    const tenantMatch = location.match(/\/tenant\/(\d+)/);
    if (tenantMatch) {
      return `/api/tenant/${tenantMatch[1]}`;
    }
    return '/api';
  };

  // Fetch all companies from database
  useEffect(() => {
    const fetchCompanies = async () => {
      try {
        const apiBaseUrl = getApiBaseUrl();
        const response = await fetch(`${apiBaseUrl}/tenant/3/companies`);
        if (response.ok) {
          const companies = await response.json();
          setAllCompanies(companies);
        }
      } catch (error) {
        console.error('Error fetching companies:', error);
      }
    };

    fetchCompanies();
  }, [location]);

  // Initialize selected companies with suggestions when pendingMappings changes
  useEffect(() => {
    const initialSelections: Record<string, string> = {};
    pendingMappings.forEach(mapping => {
      if (!selectedCompanies[mapping.driverName]) {
        initialSelections[mapping.driverName] = mapping.suggestedCompany;
      }
    });

    if (Object.keys(initialSelections).length > 0) {
      setSelectedCompanies(prev => ({ ...prev, ...initialSelections }));
    }
  }, [pendingMappings]);
  const [isProcessing, setIsProcessing] = useState(false);

  const handleCompanySelect = (driverName: string, company: string) => {
    setSelectedCompanies(prev => ({
      ...prev,
      [driverName]: company
    }));
  };

  const handleConfirmMapping = async (driverName: string) => {
    const mapping = pendingMappings.find(p => p.driverName === driverName);
    const selectedCompany = selectedCompanies[driverName] || mapping?.suggestedCompany;

    if (!selectedCompany) {
      toast({
        title: "Eroare",
        description: "VƒÉ rugƒÉm sƒÉ selecta»õi o companie pentru »ôofer",
        variant: "destructive"
      });
      return;
    }

    setIsProcessing(true);
    try {
      const result = await addDriverToDatabase(driverName, selectedCompany);
      if (result) {
        // Remove from pending mappings
        setPendingMappings(prev => prev.filter(p => p.driverName !== driverName));
        setSelectedCompanies(prev => {
          const updated = { ...prev };
          delete updated[driverName];
          return updated;
        });

        toast({
          title: "Succes", 
          description: `»òoferul "${driverName}" a fost adƒÉugat la "${selectedCompany}". Reprocesez datele...`,
          variant: "default"
        });

        // Call callback to refresh data and reprocess
        onMappingComplete();
      } else {
        throw new Error("Failed to add driver");
      }
    } catch (error) {
      toast({
        title: "Eroare",
        description: "Nu s-a putut adƒÉuga »ôoferul √Æn baza de date",
        variant: "destructive"
      });
    } finally {
      setIsProcessing(false);
    }
  };

  const handleSkipMapping = (driverName: string) => {
    setPendingMappings(prev => prev.filter(p => p.driverName !== driverName));
    setSelectedCompanies(prev => {
      const updated = { ...prev };
      delete updated[driverName];
      return updated;
    });
  };

  const handleConfirmAllWithSuggestions = async () => {
    setIsProcessing(true);
    try {
      for (const mapping of pendingMappings) {
        await addDriverToDatabase(mapping.driverName, mapping.suggestedCompany);
      }

      setPendingMappings([]);
      setSelectedCompanies({});

      toast({
        title: "Succes",
        description: `${pendingMappings.length} »ôoferi au fost adƒÉuga»õi cu sugestiile automate. Reprocesez datele...`,
        variant: "default"
      });

      onMappingComplete();
    } catch (error) {
      toast({
        title: "Eroare",
        description: "Eroare la adƒÉugarea automatƒÉ a »ôoferilor",
        variant: "destructive"
      });
    } finally {
      setIsProcessing(false);
    }
  };

  if (pendingMappings.length === 0) {
    return null;
  }

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="mb-6"
    >
      <Card className="glass-card border-orange-500/30">
        <CardHeader className="pb-4">
          <CardTitle className="flex items-center space-x-2 text-orange-400">
            <AlertCircle className="w-5 h-5" />
            <span>»òoferi Noi Detecta»õi</span>
          </CardTitle>
          <p className="text-sm text-gray-400">
            Am detectat {pendingMappings.length} »ôoferi noi care trebuie asigna»õi la companii.
          </p>
        </CardHeader>

        <CardContent className="space-y-4">
          <div className="flex justify-between items-center mb-4">
            <span className="text-sm text-gray-300">
              Asigna»õi »ôoferii la companiile potrivite:
            </span>
            <Button
              onClick={handleConfirmAllWithSuggestions}
              disabled={isProcessing}
              className="bg-green-600/20 hover:bg-green-600/30 text-green-400 border-green-500/30"
              size="sm"
            >
              <UserPlus className="w-4 h-4 mr-2" />
              AcceptƒÉ Toate Sugestiile
            </Button>
          </div>

          <AnimatePresence>
            {pendingMappings.map((mapping, index) => {
              // Use all companies from database instead of just file-processed ones
              const allDatabaseCompanies = allCompanies.map(c => c.name);
              const optionsSet = new Set([mapping.suggestedCompany, ...mapping.alternatives, ...allDatabaseCompanies]);
              const allOptions = Array.from(optionsSet);
              const selectedCompany = selectedCompanies[mapping.driverName] || mapping.suggestedCompany;

              // selectedCompany is now properly initialized via useEffect

              return (
                <motion.div
                  key={`${mapping.driverName}-${index}`}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: 20 }}
                  transition={{ delay: index * 0.1 }}
                  className="glass-card p-4 border border-orange-500/20 bg-orange-500/5"
                >
                  <div className="flex items-center justify-between">
                    <div className="flex-1">
                      <h4 className="font-medium text-white mb-2">
                        {mapping.driverName}
                      </h4>

                      <div className="flex items-center space-x-3">
                        <span className="text-sm text-gray-400">Companie:</span>
                        <Select
                          value={selectedCompany}
                          onValueChange={(value) => handleCompanySelect(mapping.driverName, value)}
                        >
                          <SelectTrigger className="w-48 glass-input">
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent className="glass-dropdown">
                            {allOptions.map((company, idx) => (
                              <SelectItem key={`${company}-${idx}`} value={company}>
                                <span className="flex items-center">
                                  {company}
                                  {company === mapping.suggestedCompany && (
                                    <span className="ml-2 text-xs text-green-400">(SugeratƒÉ)</span>
                                  )}
                                </span>
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>
                    </div>

                    <div className="flex items-center space-x-2">
                      <Button
                        onClick={() => handleConfirmMapping(mapping.driverName)}
                        disabled={isProcessing}
                        size="sm"
                        className="bg-green-600/20 hover:bg-green-600/30 text-green-400 border-green-500/30"
                      >
                        <UserPlus className="w-4 h-4 mr-1" />
                        ConfirmƒÉ
                      </Button>

                      <Button
                        onClick={() => handleSkipMapping(mapping.driverName)}
                        disabled={isProcessing}
                        size="sm"
                        variant="ghost"
                        className="text-gray-400 hover:text-red-400"
                      >
                        <X className="w-4 h-4" />
                      </Button>
                    </div>
                  </div>
                </motion.div>
              );
            })}
          </AnimatePresence>
        </CardContent>
      </Card>
    </motion.div>
  );
}

