import { motion } from "framer-motion";
import { Settings, Play, Calendar as CalendarIcon } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Progress } from "@/components/ui/progress";

interface DataProcessingSectionProps {
  selectedWeek: string;
  processingWeek: string;
  loading: boolean;
  weekOptions: any[];
  setProcessingWeek: (week: string) => void;
  setShowCalendar: (show: boolean) => void;
  processData: () => void;
  canProcess: boolean;
  processedData?: any;
  saveProcessedData: (week: string) => void;
  onShowUnmatchedModal?: () => void;
}

export function DataProcessingSection({
  selectedWeek,
  processingWeek,
  loading,
  weekOptions,
  setProcessingWeek,
  setShowCalendar,
  processData,
  canProcess,
  processedData,
  saveProcessedData,
  onShowUnmatchedModal
}: DataProcessingSectionProps) {
  return (
    <motion.div 
      className="glass-effect rounded-2xl p-8 mb-8"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: 0.4 }}
    >
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold gradient-text">Procesare Date</h2>
          <p className="text-gray-400">SelecteazƒÉ sƒÉptƒÉm√¢na »ôi proceseazƒÉ datele √ÆncƒÉrcate</p>
        </div>
        <motion.div 
          className="w-12 h-12 gradient-primary rounded-xl flex items-center justify-center animate-float"
          animate={{ y: [-10, 0, -10] }}
          transition={{ duration: 3, repeat: Infinity }}
        >
          <Settings className="text-white" />
        </motion.div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Week Selection */}
        <div className="space-y-4">
          <label className="block text-white font-medium">SelecteazƒÉ SƒÉptƒÉm√¢na</label>
          <Select value={processingWeek} onValueChange={setProcessingWeek}>
            <SelectTrigger className="w-full bg-gray-800 border-gray-600 text-white focus:border-primary">
              <SelectValue placeholder="Alege sƒÉptƒÉm√¢na..." />
            </SelectTrigger>
            <SelectContent>
              {weekOptions.map(week => (
                <SelectItem key={week.value} value={week.value}>
                  <div className="flex items-center justify-between w-full">
                    <span>{week.label}</span>
                    {week.label.includes('2024') && (
                      <span className="text-orange-400 text-xs ml-2">üìÖ 2024</span>
                    )}
                  </div>
                </SelectItem>
              ))}
            </SelectContent>
          </Select>

          {processingWeek && processingWeek.includes('2024') && (
            <div className="text-orange-400 text-xs bg-orange-400/10 p-2 rounded-lg">
              ‚ö†Ô∏è Aten»õie: Procesezi date din anul 2024
            </div>
          )}

          <Button
            onClick={() => setShowCalendar(true)}
            className="w-full bg-gradient-to-r from-blue-500 to-purple-600 hover-glow"
            variant="default"
          >
            <CalendarIcon className="mr-2" size={16} />
            Deschide Calendarul
          </Button>
        </div>

        {/* Processing Actions */}
        <div className="space-y-4">
          <label className="block text-white font-medium">Ac»õiuni de Procesare</label>
          <motion.div
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <Button
              onClick={processData}
              disabled={!canProcess || loading}
              className="w-full gradient-primary hover-glow disabled:opacity-50"
              size="lg"
            >
              <Play className="mr-2" size={16} />
              {loading ? 'Procesare...' : 'ProceseazƒÉ Datele'}
            </Button>

            {/* Manual Save Button - Only shows after processing */}
            {processedData && Object.keys(processedData).length > 0 && (
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.3 }}
                className="mt-3"
              >
                <Button
                  onClick={() => {
                    if (processingWeek) {
                      saveProcessedData(processingWeek);
                    } else {
                      alert('SelecteazƒÉ o sƒÉptƒÉm√¢nƒÉ pentru a salva datele');
                    }
                  }}
                  className="w-full bg-green-600 hover:bg-green-700 text-white"
                  size="lg"
                >
                  üíæ SalveazƒÉ √Æn DB
                </Button>
                <p className="text-xs text-gray-400 mt-2 text-center">
                  Datele sunt procesate √Æn memorie. ApasƒÉ pentru a salva √Æn baza de date.
                </p>
              </motion.div>
            )}

            {processedData && processedData.Unmatched && onShowUnmatchedModal && (
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.3 }}
              >
                <Button
                  onClick={onShowUnmatchedModal}
                  className="w-full bg-yellow-600 hover:bg-yellow-700 text-white mt-3"
                  size="lg"
                >
                  VRID Ne√Æmperecheate ({Object.keys(processedData.Unmatched.VRID_details || {}).length})
                </Button>
              </motion.div>
            )}
          </motion.div>

          {/* Processing Progress */}
          {loading && (
            <motion.div 
              className="bg-gray-800 rounded-xl p-4"
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
            >
              <div className="flex items-center justify-between mb-2">
                <span className="text-gray-300 text-sm">Progres procesare</span>
                <span className="text-primary-400 text-sm font-medium">75%</span>
              </div>
              <Progress value={75} className="w-full" />
            </motion.div>
          )}
        </div>
      </div>
    </motion.div>
  );
}

client/src/components/transport/FileUplo.content:
import { motion } from "framer-motion";
import { Upload, FileSpreadsheet, CheckCircle, CloudUpload } from "lucide-react";
import { Button } from "@/components/ui/button";

interface FileUploadSectionProps {
  tripData: any;
  invoice7Data: any;
  invoice30Data: any;
  loading: boolean;
  tripFileRef: React.RefObject<HTMLInputElement>;
  invoice7FileRef: React.RefObject<HTMLInputElement>;
  invoice30FileRef: React.RefObject<HTMLInputElement>;
  handleFileUpload: (file: File, type: string) => void;
  uploadedFiles: {[key: string]: string[]};
}

export function FileUploadSection({
  tripData,
  invoice7Data,
  invoice30Data,
  loading,
  tripFileRef,
  invoice7FileRef,
  invoice30FileRef,
  handleFileUpload,
  uploadedFiles
}: FileUploadSectionProps) {
  const uploadSections = [
    {
      type: 'trip',
      title: 'Fi»ôier TRIP',
      description: '√éncarcƒÉ fi»ôierul CSV cu datele de transport',
      icon: FileSpreadsheet,
      accept: '.csv',
      data: tripData,
      ref: tripFileRef,
      gradientClass: 'gradient-primary',
      fileName: 'trip_data.csv'
    },
    {
      type: 'invoice7',
      title: 'Facturi 7 Zile',
      description: '√éncarcƒÉ facturile cu platƒÉ la 7 zile',
      icon: FileSpreadsheet,
      accept: '.xlsx,.xls,.csv',
      data: invoice7Data,
      ref: invoice7FileRef,
      gradientClass: 'bg-green-500',
      fileName: 'invoices_7days.xlsx'
    },
    {
      type: 'invoice30',
      title: 'Facturi 30 Zile',
      description: '√éncarcƒÉ facturile cu platƒÉ la 30 zile',
      icon: FileSpreadsheet,
      accept: '.xlsx,.xls,.csv',
      data: invoice30Data,
      ref: invoice30FileRef,
      gradientClass: 'bg-orange-500',
      fileName: 'invoices_30days.xlsx'
    }
  ];

  return (
    <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 mb-8">
      {uploadSections.map((section, index) => {
        const Icon = section.icon;
        return (
          <motion.div
            key={section.type}
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
            className="glass-effect rounded-2xl p-8"
            whileHover={{ scale: 1.02 }}
          >
            <div className="text-center mb-6">
              <motion.div 
                className={`w-16 h-16 ${section.gradientClass} rounded-2xl flex items-center justify-center mx-auto mb-4 animate-pulse-glow`}
                animate={{ 
                  boxShadow: [
                    "0 0 20px rgba(139, 92, 246, 0.3)",
                    "0 0 40px rgba(139, 92, 246, 0.6)",
                    "0 0 20px rgba(139, 92, 246, 0.3)"
                  ]
                }}
                transition={{ duration: 2, repeat: Infinity }}
              >
                <Icon className="text-white" size={32} />
              </motion.div>
              <h3 className="text-xl font-bold text-white mb-2">{section.title}</h3>
              <p className="text-gray-400 text-sm">{section.description}</p>
            </div>

            <motion.div 
              className="file-upload-zone border-2 border-dashed border-gray-600 rounded-xl p-8 text-center cursor-pointer"
              whileHover={{ 
                borderColor: "hsl(262, 83%, 58%)",
                backgroundColor: "hsla(262, 83%, 58%, 0.1)"
              }}
              onClick={() => section.ref.current?.click()}
            >
              <CloudUpload className="mx-auto text-3xl text-gray-400 mb-4" size={48} />
              <p className="text-gray-300 mb-2">
                {section.type === 'invoice30' 
                  ? 'SelecteazƒÉ mai multe fi»ôiere deodatƒÉ (Ctrl+Click)'
                  : 'Drag & drop sau click pentru upload'
                }
              </p>
              <p className="text-gray-500 text-sm">{section.accept.replace(',', ', ')} files</p>
              <input
                ref={section.ref}
                type="file"
                className="hidden"
                accept={section.accept}
                multiple={section.type === 'invoice30'}
                onChange={(e) => {
                  if (section.type === 'invoice30' && e.target.files) {
                    // Pentru facturile 30 zile, permitem selec»õia multiplƒÉ
                    Array.from(e.target.files).forEach(file => {
                      handleFileUpload(file, section.type);
                    });
                  } else {
                    const file = e.target.files?.[0];
                    if (file) handleFileUpload(file, section.type);
                  }
                }}
              />
            </motion.div>

            {uploadedFiles[section.type] && uploadedFiles[section.type].length > 0 && (
              <motion.div 
                className="mt-4"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ delay: 0.3 }}
              >
                <div className="space-y-1">
                  {uploadedFiles[section.type].map((fileName, index) => (
                    <div key={index} className="flex items-center text-green-400">
                      <CheckCircle size={16} className="mr-2" />
                      <span className="text-sm">{fileName}</span>
                    </div>
                  ))}
                </div>
              </motion.div>
            )}
          </motion.div>
        );
      })}
    </div>
  );
}

