import { motion, AnimatePresence } from "framer-motion";
import { X, DollarSign, Calculator } from "lucide-react";
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";

interface PaymentModalProps {
  isOpen: boolean;
  onClose: () => void;
  company: string;
  totalAmount: number;
  sevenDaysAmount: number;
  thirtyDaysAmount: number;
  commission: number;
  alreadyPaid?: number;
  onConfirmPayment: (amount: number, description: string, type: 'partial' | 'full') => void;
}

export function PaymentModal({
  isOpen,
  onClose,
  company,
  totalAmount,
  sevenDaysAmount,
  thirtyDaysAmount,
  commission,
  alreadyPaid = 0,
  onConfirmPayment
}: PaymentModalProps) {
  const [paymentAmount, setPaymentAmount] = useState(0);
  const [description, setDescription] = useState('');
  const [paymentType, setPaymentType] = useState<'7days' | '30days' | 'custom' | 'full'>('7days');

  const handlePaymentTypeChange = (type: '7days' | '30days' | 'custom' | 'full') => {
    setPaymentType(type);
    switch (type) {
      case '7days':
        setPaymentAmount(sevenDaysAmount);
        setDescription('PlatƒÉ facturi 7 zile');
        break;
      case '30days':
        setPaymentAmount(thirtyDaysAmount);
        setDescription('PlatƒÉ facturi 30 zile');
        break;
      case 'full':
        setPaymentAmount(totalAmount);
        setDescription('PlatƒÉ completƒÉ');
        break;
      case 'custom':
        setPaymentAmount(0);
        setDescription('PlatƒÉ par»õialƒÉ personalizatƒÉ');
        break;
    }
  };

  const handleConfirm = () => {
    if (paymentAmount > 0 && paymentAmount <= totalAmount) {
      const type = paymentAmount === totalAmount ? 'full' : 'partial';
      onConfirmPayment(paymentAmount, description, type);
      onClose();
      setPaymentAmount(0);
      setDescription('');
      setPaymentType('7days');
    }
  };

  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div 
          className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          onClick={onClose}
        >
          <motion.div 
            className="glass-effect rounded-2xl p-8 max-w-lg w-full max-h-[90vh] overflow-y-auto"
            initial={{ scale: 0.8, opacity: 0, y: 50 }}
            animate={{ scale: 1, opacity: 1, y: 0 }}
            exit={{ scale: 0.8, opacity: 0, y: 50 }}
            onClick={(e) => e.stopPropagation()}
          >
            {/* Header */}
            <div className="flex items-center justify-between mb-6">
              <div className="flex items-center space-x-3">
                <div className="w-12 h-12 gradient-primary rounded-xl flex items-center justify-center">
                  <DollarSign className="text-white" size={24} />
                </div>
                <div>
                  <h3 className="text-xl font-bold text-white">√énregistreazƒÉ PlatƒÉ</h3>
                  <p className="text-gray-400">{company}</p>
                </div>
              </div>
              <motion.button
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                onClick={onClose}
                className="w-8 h-8 text-gray-400 hover:text-white transition-colors"
              >
                <X size={20} />
              </motion.button>
            </div>

            {/* Payment Summary */}
            <div className="bg-gray-800/50 rounded-xl p-4 mb-6">
              <h4 className="text-white font-medium mb-3">Detalii PlatƒÉ</h4>
              <div className="space-y-2 text-sm">
                <div className="flex justify-between">
                  <span className="text-gray-400">Facturi 7 zile:</span>
                  <span className="text-green-400">‚Ç¨{sevenDaysAmount.toFixed(2)}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-400">Facturi 30 zile:</span>
                  <span className="text-blue-400">‚Ç¨{thirtyDaysAmount.toFixed(2)}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-400">Comision:</span>
                  <span className="text-red-400">-‚Ç¨{commission.toFixed(2)}</span>
                </div>
                <div className="border-t border-gray-600 pt-2 mt-2">
                  <div className="flex justify-between font-medium">
                    <span className="text-white">Total de platƒÉ:</span>
                    <span className="text-white">‚Ç¨{totalAmount.toFixed(2)}</span>
                  </div>
                </div>
                {alreadyPaid > 0 && (
                  <div className="border-t border-gray-600 pt-2 mt-2">
                    <div className="flex justify-between text-sm">
                      <span className="text-gray-400">Deja plƒÉtit:</span>
                      <span className="text-yellow-400">‚Ç¨{alreadyPaid.toFixed(2)}</span>
                    </div>
                    <div className="flex justify-between font-medium mt-1">
                      <span className="text-white">Rest de platƒÉ:</span>
                      <span className="text-orange-400">‚Ç¨{Math.max(0, totalAmount - alreadyPaid).toFixed(2)}</span>
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* Payment Type Selection */}
            <div className="mb-6">
              <Label className="text-white font-medium mb-3 block">Tipul PlƒÉ»õii</Label>
              <div className="grid grid-cols-2 gap-3">
                <motion.button
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  onClick={() => handlePaymentTypeChange('7days')}
                  className={`p-3 rounded-xl border text-sm font-medium transition-all ${
                    paymentType === '7days'
                      ? 'border-green-500 bg-green-500/20 text-green-300'
                      : 'border-gray-600 bg-gray-800/50 text-gray-300 hover:border-gray-500'
                  }`}
                >
                  Doar 7 zile
                  <div className="text-xs mt-1">‚Ç¨{sevenDaysAmount.toFixed(2)}</div>
                </motion.button>

                <motion.button
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  onClick={() => handlePaymentTypeChange('30days')}
                  className={`p-3 rounded-xl border text-sm font-medium transition-all ${
                    paymentType === '30days'
                      ? 'border-blue-500 bg-blue-500/20 text-blue-300'
                      : 'border-gray-600 bg-gray-800/50 text-gray-300 hover:border-gray-500'
                  }`}
                >
                  Doar 30 zile
                  <div className="text-xs mt-1">‚Ç¨{thirtyDaysAmount.toFixed(2)}</div>
                </motion.button>

                <motion.button
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  onClick={() => handlePaymentTypeChange('full')}
                  className={`p-3 rounded-xl border text-sm font-medium transition-all ${
                    paymentType === 'full'
                      ? 'border-primary bg-primary/20 text-primary'
                      : 'border-gray-600 bg-gray-800/50 text-gray-300 hover:border-gray-500'
                  }`}
                >
                  PlatƒÉ totalƒÉ
                  <div className="text-xs mt-1">‚Ç¨{totalAmount.toFixed(2)}</div>
                </motion.button>

                <motion.button
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  onClick={() => handlePaymentTypeChange('custom')}
                  className={`p-3 rounded-xl border text-sm font-medium transition-all ${
                    paymentType === 'custom'
                      ? 'border-orange-500 bg-orange-500/20 text-orange-300'
                      : 'border-gray-600 bg-gray-800/50 text-gray-300 hover:border-gray-500'
                  }`}
                >
                  SumƒÉ custom
                  <div className="text-xs mt-1">PersonalizatƒÉ</div>
                </motion.button>
              </div>
            </div>

            {/* Payment Amount Input */}
            <div className="mb-4">
              <Label htmlFor="amount" className="text-white font-medium mb-2 block">
                Suma de PlatƒÉ (‚Ç¨)
              </Label>
              <div className="relative">
                <Calculator className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
                <Input
                  id="amount"
                  type="number"
                  value={paymentAmount}
                  onChange={(e) => setPaymentAmount(parseFloat(e.target.value) || 0)}
                  max={totalAmount}
                  min={0}
                  step="0.01"
                  className="pl-10 bg-gray-800 border-gray-600 text-white focus:border-primary"
                  placeholder="0.00"
                />
              </div>
              {paymentAmount > totalAmount && (
                <p className="text-red-400 text-xs mt-1">Suma nu poate fi mai mare dec√¢t totalul de platƒÉ</p>
              )}
            </div>

            {/* Description */}
            <div className="mb-6">
              <Label htmlFor="description" className="text-white font-medium mb-2 block">
                Descriere PlatƒÉ
              </Label>
              <Textarea
                id="description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                className="bg-gray-800 border-gray-600 text-white focus:border-primary"
                placeholder="Detalii despre platƒÉ..."
                rows={3}
              />
            </div>

            {/* Action Buttons */}
            <div className="flex space-x-3">
              <Button
                onClick={onClose}
                variant="outline"
                className="flex-1 border-gray-600 text-gray-300 hover:bg-gray-800"
              >
                AnuleazƒÉ
              </Button>
              <Button
                onClick={handleConfirm}
                disabled={paymentAmount <= 0 || paymentAmount > totalAmount}
                className="flex-1 gradient-primary hover-glow disabled:opacity-50"
              >
                ConfirmƒÉ Plata
              </Button>
            </div>

            {/* Remaining Amount Info */}
            {paymentAmount > 0 && paymentAmount < totalAmount && (
              <motion.div 
                className="mt-4 bg-blue-500/20 border border-blue-500/30 rounded-lg p-3"
                initial={{ opacity: 0, scale: 0.95 }}
                animate={{ opacity: 1, scale: 1 }}
              >
                <p className="text-blue-300 text-sm">
                  Restul de platƒÉ: <strong>‚Ç¨{(totalAmount - paymentAmount).toFixed(2)}</strong>
                </p>
              </motion.div>
            )}
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
}

client/src/components/transport/ResultsD.content:
import { motion } from "framer-motion";
import { Building, DollarSign, Check, Clock, Plus, Trash2, Truck, Send, CheckCircle } from "lucide-react";
import { Button } from "@/components/ui/button";
import { PaymentModal } from "./PaymentModal";
import { TransportOrderModal } from "./TransportOrderModal";
import { useState, useEffect } from "react";

interface ResultsDisplayProps {
  processedData: any;
  payments: any;
  paymentHistory: any[];
  recordPayment: (company: string, amount: number, description?: string) => void;
  deletePayment: (paymentId: number) => void;
  getRemainingPayment: (company: string) => number;
  selectedWeek: string;
}

export function ResultsDisplay({
  processedData,
  payments,
  paymentHistory,
  recordPayment,
  deletePayment,
  getRemainingPayment,
  selectedWeek
}: ResultsDisplayProps) {
  // State pentru comenzile existente
  const [existingOrders, setExistingOrders] = useState<{[key: string]: boolean}>({});

  // √éncarcƒÉ comenzile existente pentru sƒÉptƒÉm√¢na selectatƒÉ
  useEffect(() => {
    const loadExistingOrders = async () => {
      if (!selectedWeek) return;

      try {
        const response = await fetch(`/api/transport-orders?weekLabel=${encodeURIComponent(selectedWeek)}`);
        if (response.ok) {
          const orders = await response.json();
          const ordersByCompany: {[key: string]: boolean} = {};
          orders.forEach((order: any) => {
            ordersByCompany[order.companyName] = true;
          });
          setExistingOrders(ordersByCompany);
        }
      } catch (error) {
        console.error('Eroare la √ÆncƒÉrcarea comenzilor existente:', error);
      }
    };

    loadExistingOrders();
  }, [selectedWeek]);

  // Func»õie pentru verificarea dacƒÉ o companie are deja o comandƒÉ
  const hasExistingOrder = (company: string) => {
    return existingOrders[company] || false;
  };
  // üîÑ REDISTRIBUIRE AUTOMATƒÇ A VRID-URILOR UNMATCHED LA COMPANII CORECTE
  const redistributedData = { ...processedData };

  // DacƒÉ existƒÉ Unmatched, √ÆncearcƒÉ sƒÉ redistribui VRID-urile
  if (redistributedData.Unmatched && Object.keys(redistributedData.Unmatched.VRID_details || {}).length > 0) {
    console.log('‚ö†Ô∏è ATEN»öIE: ExistƒÉ VRID-uri ne√Æmperecheate care ar trebui redistribuite:');
    console.log('üîç VRID-uri Unmatched:', redistributedData.Unmatched.VRID_details);
    console.log('üí∞ Suma totalƒÉ Unmatched: ‚Ç¨' + (redistributedData.Unmatched.Total_7_days + redistributedData.Unmatched.Total_30_days).toFixed(2));
  }

  // FiltrƒÉm doar pentru afi»ôare, dar pƒÉstrƒÉm informa»õiile pentru investigare
  const companies = Object.keys(redistributedData).filter(company => company !== 'Unmatched' && company !== 'Pending Mapping');

  // Debug cleaned up - showing only relevant companies for payments

  if (!processedData || Object.keys(processedData).length === 0) {
    return (
      <div className="text-center text-gray-400 py-8">
        <p>Nu existƒÉ date procesate pentru afi»ôare</p>
      </div>
    );
  }
  const [selectedCompany, setSelectedCompany] = useState<string | null>(null);
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [showTransportOrderModal, setShowTransportOrderModal] = useState(false);

  const openPaymentModal = (company: string) => {
    setSelectedCompany(company);
    setShowPaymentModal(true);
  };

  const openTransportOrderModal = (company: string) => {
    setSelectedCompany(company);
    setShowTransportOrderModal(true);
  };

  const handleConfirmPayment = (amount: number, description: string, type: 'partial' | 'full') => {
    if (selectedCompany) {
      recordPayment(selectedCompany, amount, description);
    }
  };

  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
      {/* Company Breakdown */}
      <motion.div 
        className="glass-effect rounded-2xl p-8"
        initial={{ opacity: 0, x: -50 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ delay: 0.5 }}
      >
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-xl font-bold text-white">Breakdown pe Companii</h3>
          <div className="w-10 h-10 bg-green-500 rounded-xl flex items-center justify-center">
            <Building className="text-white" size={20} />
          </div>
        </div>

        <div className="space-y-4">
          {companies.map((company, index) => {
            const data = redistributedData[company];
            const totalAmount = data.Total_7_days + data.Total_30_days - data.Total_comision;
            const driversCount = Object.keys(data.VRID_details || {}).length;
            const commissionRate = company === "Fast Express" ? "2%" : "4%";

            // Check if company has been paid for current week
            const currentWeekPayments = paymentHistory.filter(payment => 
              payment.company === company && payment.week === selectedWeek
            );
            const totalPaidThisWeek = currentWeekPayments.reduce((sum, payment) => sum + payment.amount, 0);
            const hasBeenPaid = totalPaidThisWeek > 0;

            return (
              <motion.div
                key={company}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.6 + index * 0.1 }}
                className="data-card rounded-xl p-4"
                whileHover={{ y: -5 }}
              >
                <div className="flex items-center justify-between">
                  <div className="flex-1">
                    <h4 className="font-medium text-white">{company}</h4>
                    <p className="text-gray-400 text-sm">{driversCount} »ôoferi</p>
                    <div className="flex space-x-4 mt-1 text-xs">
                      <span className="text-green-400">7z: ‚Ç¨{data.Total_7_days.toFixed(0)}</span>
                      <span className="text-blue-400">30z: ‚Ç¨{data.Total_30_days.toFixed(0)}</span>
                      <span className="text-red-400">Com: ‚Ç¨{data.Total_comision.toFixed(0)}</span>
                    </div>
                  </div>
                  <div className="flex items-center space-x-3">
                    <div className="text-right">
                      <p className="text-lg font-bold text-green-400">‚Ç¨{totalAmount.toFixed(2)}</p>
                      <p className="text-gray-400 text-sm">Comision {commissionRate}</p>
                    </div>
                    <div className="flex items-center space-x-2">
                      {hasBeenPaid ? (
                        <div className="px-3 py-2 bg-green-600/20 border-2 border-green-500/30 rounded-lg text-green-400 text-sm font-medium flex items-center space-x-1" title={`PlƒÉtit: ‚Ç¨${totalPaidThisWeek.toFixed(2)} √Æn aceastƒÉ sƒÉptƒÉm√¢nƒÉ`}>
                          <CheckCircle className="w-4 h-4" />
                          <span className="text-xs">PlƒÉtit</span>
                        </div>
                      ) : (
                        <motion.button
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          onClick={() => openPaymentModal(company)}
                          className="px-3 py-2 gradient-primary rounded-lg text-white text-sm font-medium hover-glow"
                        >
                          PlƒÉte»ôte
                        </motion.button>
                      )}
                      {hasExistingOrder(company) ? (
                        <div className="px-3 py-2 bg-green-600/20 border-2 border-green-500/30 rounded-lg text-green-400 text-sm font-medium flex items-center space-x-1" title="ComandƒÉ deja generatƒÉ">
                          <CheckCircle className="w-4 h-4" />
                          <span className="text-xs">ComandƒÉ existƒÉ</span>
                        </div>
                      ) : (
                        <motion.button
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          onClick={() => openTransportOrderModal(company)}
                          className="px-3 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg text-white text-sm font-medium border-2 border-blue-400"
                          title="GenereazƒÉ comandƒÉ de transport"
                        >
                          <Truck className="w-4 h-4" />
                          <span className="ml-1 text-xs">ComandƒÉ</span>
                        </motion.button>
                      )}
                    </div>
                  </div>
                </div>
              </motion.div>
            );
          })}
        </div>
      </motion.div>

      {/* Payment Management */}
      <motion.div 
        className="glass-effect rounded-2xl p-8"
        initial={{ opacity: 0, x: 50 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ delay: 0.6 }}
      >
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-xl font-bold text-white">Eviden»õa PlƒÉ»õilor</h3>
          <div className="w-10 h-10 bg-blue-500 rounded-xl flex items-center justify-center">
            <DollarSign className="text-white" size={20} />
          </div>
        </div>

        <div className="space-y-4">
          {paymentHistory.slice(0, 5).map((payment, index) => (
            <motion.div
              key={payment.id}
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: 0.7 + index * 0.1 }}
              className="data-card rounded-xl p-4"
              whileHover={{ scale: 1.02 }}
            >
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <div className="w-10 h-10 bg-green-500 rounded-full flex items-center justify-center">
                    <Check className="text-white" size={16} />
                  </div>
                  <div>
                    <p className="font-medium text-white">{payment.company}</p>
                    <p className="text-gray-400 text-sm">{payment.date}</p>
                    {payment.description && (
                      <p className="text-gray-500 text-xs">{payment.description}</p>
                    )}
                  </div>
                </div>
                <div className="flex items-center space-x-3">
                  <div className="text-right">
                    <p className="font-bold text-green-400">‚Ç¨{payment.amount}</p>
                    <p className="text-gray-400 text-sm">Completed</p>
                  </div>
                  <motion.button
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                    onClick={() => deletePayment(payment.id)}
                    className="w-8 h-8 bg-red-500/20 hover:bg-red-500 rounded-lg flex items-center justify-center transition-colors"
                  >
                    <Trash2 className="text-red-400 hover:text-white" size={14} />
                  </motion.button>
                </div>
              </div>
            </motion.div>
          ))}

          {paymentHistory.length === 0 && (
            <div className="text-center text-gray-400 py-8">
              <Clock size={48} className="mx-auto mb-4 opacity-50" />
              <p>Nu existƒÉ plƒÉ»õi √Ænregistrate √ÆncƒÉ</p>
            </div>
          )}
        </div>

        {companies.length > 0 && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 1 }}
            className="mt-6"
          >
            <Button
              onClick={() => openPaymentModal(companies[0])}
              className="w-full gradient-primary hover-glow"
              disabled={companies.length === 0}
            >
              <Plus className="mr-2" size={16} />
              √énregistreazƒÉ PlatƒÉ
            </Button>
          </motion.div>
        )}
      </motion.div>

      {/* Payment Modal */}
      {selectedCompany && (
        <PaymentModal
          isOpen={showPaymentModal}
          onClose={() => {
            setShowPaymentModal(false);
            setSelectedCompany(null);
          }}
          company={selectedCompany}
          totalAmount={
            processedData[selectedCompany]?.Total_7_days + 
            processedData[selectedCompany]?.Total_30_days - 
            processedData[selectedCompany]?.Total_comision || 0
          }
          sevenDaysAmount={processedData[selectedCompany]?.Total_7_days || 0}
          thirtyDaysAmount={processedData[selectedCompany]?.Total_30_days || 0}
          commission={processedData[selectedCompany]?.Total_comision || 0}
          alreadyPaid={payments[selectedCompany] || 0}
          onConfirmPayment={handleConfirmPayment}
        />
      )}

      {/* Transport Order Modal */}
      {selectedCompany && (
        <TransportOrderModal
          isOpen={showTransportOrderModal}
          onClose={() => {
            setShowTransportOrderModal(false);
            setSelectedCompany(null);
          }}
          company={selectedCompany}
          processedData={processedData}
          selectedWeek={selectedWeek}
        />
      )}
    </div>
  );
}

