/**
 * üîí ISOLATION ENFORCER - GaranteazƒÉ separarea completƒÉ a datelor per tenant
 */
import type { Express, Request, Response, NextFunction } from "express";
import type { IStorage } from "./storage.js";

export interface TenantRequest extends Request {
  user?: {
    id: number;
    username: string;
    email: string;
    tenantId?: string;
    role?: string;
  };
  tenantId?: string;
  tenantStorage?: IStorage;
}

/**
 * Middleware care detecteazƒÉ »ôi seteazƒÉ tenant-ul pentru fiecare request
 */
export function createTenantDetectionMiddleware(storage: IStorage) {
  return async (req: TenantRequest, res: Response, next: NextFunction) => {
    try {
      // Skip pentru rute de autentificare »ôi publice
      if (req.path === '/api/login' || 
          req.path === '/api/register' || 
          req.path === '/api/auth/user' ||
          req.path === '/api/logout' ||
          req.path === '/api/auth/check-username' ||
          req.path === '/api/auth/check-email' ||
          req.path === '/api/auth/register' ||
          req.path === '/api/create-subscription' ||
          req.path === '/api/verify-card' ||
          req.path === '/api/cancel-payment' ||
          req.path.startsWith('/api/pricing') ||
          req.path.startsWith('/api/health') ||
          req.path.startsWith('/api/stripe') ||
          req.path.startsWith('/pricing') ||
          req.path.startsWith('/health') ||
          req.path === '/' ||
          req.path.startsWith('/assets/') ||
          req.path.startsWith('/src/') ||
          req.path.includes('.js') ||
          req.path.includes('.css') ||
          req.path.includes('.png') ||
          req.path.includes('.svg') ||
          req.path.includes('vite') ||
          req.path.includes('@')) {
        return next();
      }

      // VerificƒÉ autentificarea
      if (!req.session?.userId) {
        return res.status(401).json({ 
          error: 'Not authenticated',
          isolation: 'ENFORCED'
        });
      }

      // Ob»õine utilizatorul din baza de date - PRIORITIZE SUPABASE pentru utilizatori noi
      let user = null;

      // Pentru petrisor (ID: 1), √ÆncearcƒÉ direct √Æn SupabaseMainStorage
      if (req.session.userId === 1) {
        try {
          const { SupabaseMainStorage } = await import('./supabase-main-storage.js');
          const { createClient } = await import('@supabase/supabase-js');
          const supabase = createClient(process.env.SUPABASE_URL!, process.env.SUPABASE_KEY!);
          const supabaseMainStorage = new SupabaseMainStorage(supabase);
          user = await supabaseMainStorage.getUser(req.session.userId);

          if (user) {
            console.log(`üéØ ISOLATION: Found user ${user.username} (ID: ${user.id}) in Supabase`);
          }
        } catch (error) {
          console.warn('Could not check Supabase for user 1:', error);
        }
      }

      // DacƒÉ nu se gƒÉse»ôte √Æn Supabase, verificƒÉ √Æn storage-ul vechi
      if (!user) {
        user = await storage.getUser(req.session.userId);
        if (user) {
          console.log(`üéØ ISOLATION: Found user ${user.username} (ID: ${user.id}) in legacy storage`);
        }
      }

      if (!user) {
        return res.status(401).json({ 
          error: 'User not found',
          isolation: 'ENFORCED'
        });
      }

      // SeteazƒÉ tenant-ul √Æn request
      req.user = user;
      req.tenantId = user.tenantId || 'main';

      // Log pentru debugging
      console.log(`üîí ISOLATION: User ${user.username} (ID: ${user.id}) ‚Üí Tenant: ${req.tenantId}`);

      // Pentru tenant-ii cu schema separatƒÉ, ob»õine storage-ul dedicat
      if (user.tenantId && user.tenantId !== 'main') {
        try {
          const { TenantStorageSimple } = await import('./tenant-storage-simple.js');
          req.tenantStorage = new TenantStorageSimple(user.tenantId);
          console.log(`‚úÖ ISOLATION: TenantStorageSimple loaded for ${user.tenantId} - PERFECT ISOLATION WITH SQL IDENTIFIERS`);
        } catch (error) {
          console.error(`‚ùå ISOLATION: Failed to load tenant storage for ${user.tenantId}:`, error);
          return res.status(500).json({ 
            error: 'Tenant isolation failed',
            tenantId: user.tenantId,
            isolation: 'CRITICAL_ERROR'
          });
        }
      }

      next();
    } catch (error) {
      console.error('‚ùå ISOLATION: Tenant detection failed:', error);
      res.status(500).json({ 
        error: 'Isolation enforcement failed',
        details: error instanceof Error ? error.message : 'Unknown error'
      });
    }
  };
}

/**
 * Ob»õine storage-ul corect bazat pe tenant
 */
export function getTenantStorage(req: TenantRequest, mainStorage: IStorage): IStorage {
  // Pentru tenant-ii cu schema separatƒÉ, folose»ôte storage-ul dedicat
  if (req.tenantStorage) {
    console.log(`üîí Using tenant storage for: ${req.tenantId}`);
    return req.tenantStorage;
  }

  // Pentru utilizatorul principal sau fallback
  console.log(`üîí Using main storage for: ${req.tenantId || 'unknown'}`);
  return mainStorage;
}

/**
 * VerificƒÉ »ôi raporteazƒÉ izolarea
 */
export function logIsolationStatus(req: TenantRequest, operation: string, dataCount: number) {
  const isolation = req.tenantStorage ? 'TENANT_SCHEMA' : 'MAIN_DATABASE';
  console.log(`üîí ISOLATION: ${operation} ‚Üí User: ${req.user?.username} ‚Üí Tenant: ${req.tenantId} ‚Üí Storage: ${isolation} ‚Üí Records: ${dataCount}`);
}

/**
 * ValideazƒÉ cƒÉ nu existƒÉ data leakage
 */
export function validateNoDataLeakage(req: TenantRequest, data: any[], operation: string) {
  if (!req.user) {
    throw new Error('User not found in request - isolation violation');
  }

  // Pentru tenant-ii cu schema separatƒÉ, toate datele trebuie sƒÉ aibƒÉ tenant_id corect
  if (req.tenantStorage && data.length > 0) {
    const invalidRecords = data.filter(record => 
      record.tenantId && record.tenantId !== req.tenantId
    );

    if (invalidRecords.length > 0) {
      console.error(`‚ùå DATA LEAKAGE DETECTED: ${operation} ‚Üí Expected tenant: ${req.tenantId}, Found: ${invalidRecords.map(r => r.tenantId).join(', ')}`);
      throw new Error(`Data leakage detected: ${invalidRecords.length} records from other tenants`);
    }
  }

  console.log(`‚úÖ ISOLATION VALIDATED: ${operation} ‚Üí ${data.length} records ‚Üí No leakage detected`);
}

