import { SupabaseClient } from '@supabase/supabase-js';
import { 
  type User, 
  type InsertUser,
  type Company,
  type InsertCompany,
  type Driver,
  type InsertDriver,
  type WeeklyProcessing,
  type InsertWeeklyProcessing,
  type Payment,
  type InsertPayment,
  type PaymentHistoryRecord,
  type InsertPaymentHistory,
  type TransportOrder,
  type InsertTransportOrder,
  type HistoricalTrip,
  type InsertHistoricalTrip,
  type OrderSequence,
  type InsertOrderSequence,
  type CompanyBalance,
  type InsertCompanyBalance
} from "@shared/schema";

/**
 * Storage pentru utilizatorul principal folosind Supabase
 * √énlocuie»ôte DatabaseStorage cu implementare Supabase
 */
export class SupabaseMainStorage {
  private supabase: SupabaseClient;

  constructor(supabase: SupabaseClient) {
    this.supabase = supabase;
  }

  // ================== User Methods ==================
  async getUser(id: number): Promise<User | undefined> {
    try {
      const { data, error } = await this.supabase
        .from('users')
        .select('*')
        .eq('id', id)
        .single();

      if (error && error.code !== 'PGRST116') throw error;

      if (data) {
        // Map Supabase response to Drizzle schema
        return {
          ...data,
          password: data.password_hash, // Map password_hash to password
          tenantId: data.tenant_id // Map tenant_id to tenantId
        } as User;
      }

      return undefined;
    } catch (error) {
      console.error(`‚ùå Error fetching user ${id}:`, error);
      return undefined;
    }
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    try {
      const { data, error } = await this.supabase
        .from('users')
        .select('*')
        .eq('username', username)
        .single();

      if (error && error.code !== 'PGRST116') throw error;

      if (data) {
        // Map Supabase response to Drizzle schema
        return {
          ...data,
          password: data.password_hash, // Map password_hash to password
          tenantId: data.tenant_id // Map tenant_id to tenantId
        } as User;
      }

      return undefined;
    } catch (error) {
      console.error(`‚ùå Error fetching user by username ${username}:`, error);
      return undefined;
    }
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    try {
      const { data, error } = await this.supabase
        .from('users')
        .select('*')
        .eq('email', email)
        .single();

      if (error && error.code !== 'PGRST116') throw error;
      return data || undefined;
    } catch (error) {
      console.error(`‚ùå Error fetching user by email ${email}:`, error);
      return undefined;
    }
  }

  async createUser(user: InsertUser): Promise<User> {
    try {
      // Map Drizzle schema field names to Supabase schema field names
      const supabaseUser = {
        username: user.username,
        password_hash: user.password, // Map password to password_hash
        email: user.email,
        role: user.role,
        tenant_id: user.tenantId, // Map tenantId to tenant_id
        subscription_status: user.subscriptionStatus,
        subscription_plan: 'transport_pro',
        subscription_start_date: new Date().toISOString()
      };

      const { data, error } = await this.supabase
        .from('users')
        .insert(supabaseUser)
        .select()
        .single();

      if (error) throw error;

      // Map Supabase response back to Drizzle schema
      return {
        ...data,
        password: data.password_hash, // Map password_hash back to password
        tenantId: data.tenant_id // Map tenant_id back to tenantId
      } as User;
    } catch (error) {
      console.error(`‚ùå Error creating user:`, error);
      throw error;
    }
  }

  // ================== Company Methods ==================
  async getAllCompanies(): Promise<Company[]> {
    try {
      const { data, error } = await this.supabase
        .from('companies')
        .select('*')
        .eq('tenant_id', 'main')
        .order('name');

      if (error) throw error;
      return data || [];
    } catch (error) {
      console.error(`‚ùå Error fetching companies:`, error);
      return [];
    }
  }

  async getCompanyByName(name: string): Promise<Company | undefined> {
    try {
      const { data, error } = await this.supabase
        .from('companies')
        .select('*')
        .eq('name', name)
        .eq('tenant_id', 'main')
        .single();

      if (error && error.code !== 'PGRST116') throw error;
      return data || undefined;
    } catch (error) {
      console.error(`‚ùå Error fetching company ${name}:`, error);
      return undefined;
    }
  }

  async createCompany(company: InsertCompany): Promise<Company> {
    try {
      // Map camelCase fields to Supabase snake_case schema
      const companyData = {
        name: company.name,
        commission_rate: parseFloat(company.commissionRate || '0.04'), // Correct field name for Supabase
        cif: company.cif,
        trade_registry: company.tradeRegisterNumber,
        address: company.address,
        tenant_id: 'main'
      };

      const { data, error } = await this.supabase
        .from('companies')
        .insert(companyData)
        .select()
        .single();

      if (error) throw error;

      // Map response back to camelCase for consistency
      return {
        id: data.id,
        name: data.name,
        commissionRate: data.commission_rate?.toString() || '0.04',
        cif: data.cif,
        tradeRegisterNumber: data.trade_registry,
        address: data.address,
        location: data.location,
        county: data.county,
        country: data.country || 'Romania',
