import { createClient, SupabaseClient } from '@supabase/supabase-js';

interface TenantSupabaseConfig {
  supabase: SupabaseClient;
  tenantId: string;
  tablePrefix: string;
}

/**
 * Manager pentru baze de date multi-tenant cu Supabase
 * Fiecare tenant va avea propriile tabele cu prefix pentru izolare completƒÉ
 */
class SupabaseMultiTenantManager {
  private tenantClients: Map<string, TenantSupabaseConfig> = new Map();
  private mainSupabase: SupabaseClient;

  constructor() {
    const supabaseUrl = process.env.SUPABASE_URL!;
    const supabaseKey = process.env.SUPABASE_KEY!;

    console.log('üîó Initializing Supabase Multi-Tenant Manager');
    console.log(`üì° Supabase URL: ${supabaseUrl}`);

    this.mainSupabase = createClient(supabaseUrl, supabaseKey);
  }

  /**
   * Ob»õine clientul Supabase principal pentru autentificare »ôi gestionarea utilizatorilor
   */
  getMainSupabase() {
    return this.mainSupabase;
  }

  /**
   * CreeazƒÉ structura de tabele pentru un tenant nou √Æn Supabase
   */
  private async initializeTenantTables(tenantId: string): Promise<void> {
    console.log(`üî® Initializing tenant structure for: ${tenantId}`);

    try {
      const tablePrefix = `tenant_${tenantId}_`;

      // Pentru acum, ini»õializƒÉm structura virtualƒÉ
      // √én Supabase, vom folosi Row Level Security (RLS) pentru izolarea datelor
      console.log(`‚úÖ Tenant structure initialized for: ${tenantId} with prefix: ${tablePrefix}`);
    } catch (error) {
      console.error(`‚ùå Failed to initialize tenant structure for ${tenantId}:`, error);
      throw error;
    }
  }

  /**
   * CreeazƒÉ sau ob»õine clientul Supabase pentru un tenant
   */
  async getTenantSupabase(tenantId: string): Promise<TenantSupabaseConfig> {
    if (this.tenantClients.has(tenantId)) {
      return this.tenantClients.get(tenantId)!;
    }

    console.log(`üîÑ Creating new Supabase client for tenant: ${tenantId}`);

    try {
      // CreeazƒÉ client Supabase pentru tenant cu configura»õie dedicatƒÉ
      const tenantSupabase = createClient(
        process.env.SUPABASE_URL!,
        process.env.SUPABASE_KEY!
      );

      const tablePrefix = `tenant_${tenantId}_`;

      // Ini»õializeazƒÉ structura tenant-ului
      await this.initializeTenantTables(tenantId);

      const tenantConfig: TenantSupabaseConfig = {
        supabase: tenantSupabase,
        tenantId,
        tablePrefix
      };

      this.tenantClients.set(tenantId, tenantConfig);

      console.log(`‚úÖ Successfully created tenant Supabase client for: ${tenantId}`);
      return tenantConfig;

    } catch (error) {
      console.error(`‚ùå Failed to create tenant Supabase client for ${tenantId}:`, error);
      throw error;
    }
  }

  /**
   * Ob»õine clientul Supabase pentru un tenant specific
   */
  async getTenantClient(tenantId: string): Promise<SupabaseClient> {
    const tenantConfig = await this.getTenantSupabase(tenantId);
    return tenantConfig.supabase;
  }

  /**
   * √énchide toate conexiunile tenant-ilor
   */
  async closeAllTenantConnections(): Promise<void> {
    console.log('üîÑ Closing all tenant Supabase connections...');

    const tenantIds = Array.from(this.tenantClients.keys());
    for (const tenantId of tenantIds) {
      try {
        console.log(`üîí Closing connection for tenant: ${tenantId}`);
      } catch (error) {
        console.error(`‚ùå Error closing connection for tenant ${tenantId}:`, error);
      }
    }

    this.tenantClients.clear();
    console.log('‚úÖ All tenant connections closed');
  }

  /**
   * »òterge complet datele unui tenant (pentru cleanup)
   */
  async deleteTenantData(tenantId: string): Promise<void> {
    try {
      console.log(`üóëÔ∏è Deleting tenant data for: ${tenantId}`);

      const tenantConfig = this.tenantClients.get(tenantId);
      if (tenantConfig) {
        // √én viitor, aici vom »ôterge toate datele tenant-ului din tabele
        this.tenantClients.delete(tenantId);
        console.log(`‚úÖ Successfully deleted tenant data for: ${tenantId}`);
      }
    } catch (error) {
      console.error(`‚ùå Failed to delete tenant data for ${tenantId}:`, error);
      throw error;
    }
  }

  /**
   * Ob»õine statistici despre tenant-ii activi
   */
  getTenantStats(): { activeTenants: number; tenantIds: string[] } {
    return {
      activeTenants: this.tenantClients.size,
      tenantIds: Array.from(this.tenantClients.keys())
    };
  }
}

// Instan»õƒÉ singleton
const supabaseMultiTenantManager = new SupabaseMultiTenantManager();

export default supabaseMultiTenantManager;
export { SupabaseMultiTenantManager };

