import supabaseMultiTenantManager from './supabase-multi-tenant-manager.js';
import { SupabaseStorage } from './supabase-storage.js';

/**
 * Enhanced multi-tenant manager cu suport Supabase
 * OferƒÉ izolare completƒÉ prin database-uri separate
 */
class SupabaseTenantManager {
  private tenantStorages: Map<string, SupabaseStorage> = new Map();

  /**
   * Ob»õine storage-ul pentru un tenant folosind Supabase
   */
  async getTenantStorage(tenantId: string): Promise<SupabaseStorage> {
    // VerificƒÉ dacƒÉ storage-ul existƒÉ deja √Æn cache
    if (this.tenantStorages.has(tenantId)) {
      return this.tenantStorages.get(tenantId)!;
    }

    console.log(`üîÑ Creating Supabase storage for tenant: ${tenantId}`);

    try {
      // Ob»õine clientul Supabase pentru tenant
      const tenantClient = await supabaseMultiTenantManager.getTenantClient(tenantId);

      // CreeazƒÉ storage-ul Supabase pentru tenant
      const tenantStorage = new SupabaseStorage(tenantClient, tenantId);

      // AdaugƒÉ √Æn cache
      this.tenantStorages.set(tenantId, tenantStorage);

      console.log(`‚úÖ Supabase storage created for tenant: ${tenantId}`);
      return tenantStorage;

    } catch (error) {
      console.error(`‚ùå Failed to create Supabase storage for tenant ${tenantId}:`, error);
      throw error;
    }
  }

  /**
   * √énchide toate conexiunile tenant-ilor
   */
  async closeAllTenantConnections(): Promise<void> {
    console.log('üîÑ Closing all Supabase tenant storages...');

    // √énchide storage-urile
    this.tenantStorages.clear();

    // √énchide conexiunile Supabase
    await supabaseMultiTenantManager.closeAllTenantConnections();

    console.log('‚úÖ All Supabase tenant connections closed');
  }

  /**
   * Ob»õine statistici despre tenant-ii activi
   */
  getTenantStats(): { activeTenants: number; tenantIds: string[] } {
    return {
      activeTenants: this.tenantStorages.size,
      tenantIds: Array.from(this.tenantStorages.keys())
    };
  }

  /**
   * »òterge complet datele unui tenant
   */
  async deleteTenantData(tenantId: string): Promise<void> {
    try {
      console.log(`üóëÔ∏è Deleting Supabase tenant data for: ${tenantId}`);

      // EliminƒÉ din cache
      this.tenantStorages.delete(tenantId);

      // »òterge datele din Supabase
      await supabaseMultiTenantManager.deleteTenantData(tenantId);

      console.log(`‚úÖ Successfully deleted Supabase tenant data for: ${tenantId}`);
    } catch (error) {
      console.error(`‚ùå Failed to delete Supabase tenant data for ${tenantId}:`, error);
      throw error;
    }
  }
}

// Instan»õƒÉ singleton
const supabaseTenantManager = new SupabaseTenantManager();

export { supabaseTenantManager, SupabaseTenantManager };

