import { useState } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Calendar, CreditCard, Search, Filter, Download, Trash2, AlertTriangle, FileText } from "lucide-react";
import { jsPDF } from 'jspdf';
import autoTable from 'jspdf-autotable';
import { motion } from "framer-motion";
import type { Payment } from "@shared/schema";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

const formatCurrency = (amount: number): string => {
  return new Intl.NumberFormat('ro-RO', {
    style: 'currency',
    currency: 'EUR',
    minimumFractionDigits: 2,
  }).format(amount);
};

const formatDate = (date: string): string => {
  return new Intl.DateTimeFormat('ro-RO', {
    year: 'numeric',
    month: 'short',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
  }).format(new Date(date));
};

// Helper function to fix Romanian diacritics for PDF generation
const fixRomanianText = (text: string): string => {
  return text
    .replace(/ƒÉ/g, 'a')
    .replace(/√¢/g, 'a') 
    .replace(/√Æ/g, 'i')
    .replace(/»ô/g, 's')
    .replace(/»õ/g, 't')
    .replace(/ƒÇ/g, 'A')
    .replace(/√Ç/g, 'A')
    .replace(/√é/g, 'I')
    .replace(/»ò/g, 'S')
    .replace(/»ö/g, 'T');
};

interface DeletePaymentModalProps {
  payment: Payment;
  isOpen: boolean;
  onClose: () => void;
}

function DeletePaymentModal({ payment, isOpen, onClose }: DeletePaymentModalProps) {
  const { toast } = useToast();

  const deleteMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch(`/api/payments/${payment.id}`, {
        method: "DELETE",
      });
      if (!response.ok) {
        throw new Error('Failed to delete payment');
      }
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "PlatƒÉ »ôtearsƒÉ",
        description: `Plata de ${formatCurrency(parseFloat(payment.amount))} pentru ${payment.companyName} a fost »ôtearsƒÉ.`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/payments'] });
      queryClient.invalidateQueries({ queryKey: ['/api/company-balances'] });
      onClose();
    },
    onError: (error) => {
      toast({
        title: "Eroare la »ôtergerea plƒÉ»õii",
        description: "Nu s-a putut »ôterge plata. √éncerca»õi din nou.",
        variant: "destructive",
      });
    }
  });

  const handleDelete = () => {
    deleteMutation.mutate();
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[425px]">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <AlertTriangle className="h-5 w-5 text-red-500" />
            »òtergere PlatƒÉ
          </DialogTitle>
          <DialogDescription>
            Sigur dori»õi sƒÉ »ôterge»õi aceastƒÉ platƒÉ definitiv?
          </DialogDescription>
        </DialogHeader>
        <div className="py-4">
          <div className="space-y-2">
            <div><strong>Companie:</strong> {payment.companyName}</div>
            <div><strong>SƒÉptƒÉm√¢na:</strong> {payment.weekLabel}</div>
            <div><strong>Suma:</strong> {formatCurrency(parseFloat(payment.amount))}</div>
            <div><strong>Data:</strong> {formatDate(payment.paymentDate)}</div>
            {payment.description && <div><strong>Descriere:</strong> {payment.description}</div>}
          </div>
          <div className="bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-lg p-3 mt-4">
            <div className="text-sm text-red-600 dark:text-red-400">
              ‚ö†Ô∏è AceastƒÉ ac»õiune este permanentƒÉ »ôi va recalcula automat bilan»õele companiei.
            </div>
          </div>
        </div>
        <DialogFooter>
          <Button type="button" variant="outline" onClick={onClose}>
            Anulare
          </Button>
          <Button 
            onClick={handleDelete} 
            variant="destructive" 
            disabled={deleteMutation.isPending}
          >
            {deleteMutation.isPending ? "Se »ôterge..." : "»òterge definitiv"}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

export default function PaymentHistoryView() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCompany, setSelectedCompany] = useState<string>("all");
  const [selectedWeek, setSelectedWeek] = useState<string>("all");
  const [selectedPayment, setSelectedPayment] = useState<Payment | null>(null);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const { toast } = useToast();

  const { data: payments = [], isLoading } = useQuery({
    queryKey: ['/api/payments'],
    queryFn: async () => {
      const response = await fetch('/api/payments');
      if (!response.ok) {
        throw new Error('Failed to fetch payments');
      }
      return response.json();
    },
    refetchInterval: 5000, // Refresh more frequently
  }) as { data: Payment[], isLoading: boolean };

  console.log('üí≥ Payments loaded:', payments.length, 'payments');

  // Fetch weekly processing data to get 7-day and 30-day details
  const { data: weeklyData = [] } = useQuery({
    queryKey: ['/api/weekly-processing'],
    queryFn: async () => {
      const response = await fetch('/api/weekly-processing');
      if (!response.ok) {
        throw new Error('Failed to fetch weekly data');
      }
      return response.json();
    },
    refetchInterval: 60000,
  });

  // Get unique companies and weeks for filters
  const uniqueCompanies = [...new Set(payments.map(p => p.companyName))].sort();
  const uniqueWeeks = [...new Set(payments.map(p => p.weekLabel))].sort();

  // Filter payments
  const filteredPayments = payments.filter(payment => {
    const matchesSearch = payment.companyName.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         payment.weekLabel.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (payment.description && payment.description.toLowerCase().includes(searchTerm.toLowerCase()));

    const matchesCompany = selectedCompany === "all" || payment.companyName === selectedCompany;
    const matchesWeek = selectedWeek === "all" || payment.weekLabel === selectedWeek;

    return matchesSearch && matchesCompany && matchesWeek;
  }).sort((a, b) => new Date(a.paymentDate).getTime() - new Date(b.paymentDate).getTime()); // Latest payments at the bottom

  // Group payments by week
  const groupedByWeek = filteredPayments.reduce((acc, payment) => {
    const week = payment.weekLabel;
    if (!acc[week]) {
      acc[week] = [];
    }
    acc[week].push(payment);
    return acc;
  }, {} as Record<string, Payment[]>);

  // Sort weeks by most recent first
  const sortedWeeks = Object.keys(groupedByWeek).sort((a, b) => {
    const weekA = groupedByWeek[a][0];
    const weekB = groupedByWeek[b][0];
    return new Date(weekB.paymentDate).getTime() - new Date(weekA.paymentDate).getTime();
  });

  // Get weekly processing details for a specific week and company
  const getWeeklyDetails = (weekLabel: string, companyName: string) => {
    const weekData = weeklyData.find((w: any) => w.weekLabel === weekLabel);
    if (!weekData || !weekData.processedData) return null;

    const companyData = weekData.processedData[companyName];
    if (!companyData) return null;

    return {
      total7Days: companyData.Total_7_days || 0,
      total30Days: companyData.Total_30_days || 0,
      totalCommission: companyData.Total_comision || 0,
      totalInvoiced: (companyData.Total_7_days || 0) + (companyData.Total_30_days || 0),
      totalToPay: (companyData.Total_7_days || 0) + (companyData.Total_30_days || 0) - (companyData.Total_comision || 0)
    };
  };

  // Calculate totals
  const totalAmount = filteredPayments.reduce((sum, payment) => sum + parseFloat(payment.amount), 0);
  const totalByCompany = filteredPayments.reduce((acc, payment) => {
    acc[payment.companyName] = (acc[payment.companyName] || 0) + parseFloat(payment.amount);
    return acc;
  }, {} as Record<string, number>);

  const handleDeleteClick = (payment: Payment) => {
    setSelectedPayment(payment);
    setIsDeleteModalOpen(true);
  };

  const exportToCSV = () => {
    const headers = ['Data', 'Companie', 'SƒÉptƒÉm√¢na', 'Suma', 'Descriere'];
    const csvData = [
      headers.join(','),
      ...filteredPayments.map(payment => [
        formatDate(payment.paymentDate),
        payment.companyName,
        payment.weekLabel,
        payment.amount,
        payment.description || ''
      ].map(field => `"${field}"`).join(','))
    ].join('\n');

    const blob = new Blob([csvData], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = `istoric_plati_${new Date().toISOString().split('T')[0]}.csv`;
    link.click();
  };

  const generatePDF = () => {
    const doc = new jsPDF();
    const pageHeight = doc.internal.pageSize.height;

    // Logo »ôi antet
    doc.setFontSize(20);
    doc.setTextColor(40, 40, 40);
    doc.text('SISTEMA TRANSPORT', 20, 25);

    doc.setFontSize(14);
    doc.setTextColor(100, 100, 100);
    doc.text(fixRomanianText('Istoric PlƒÉ»õi - Situa»õia CompletƒÉ'), 20, 35);

    doc.setFontSize(10);
    doc.text(fixRomanianText(`Generat: ${new Date().toLocaleDateString('ro-RO')} ${new Date().toLocaleTimeString('ro-RO')}`), 20, 45);

    // Linie separatoare
    doc.setLineWidth(0.5);
    doc.setDrawColor(200, 200, 200);
    doc.line(20, 50, 190, 50);

    let yPosition = 65;

    // Sumar general
    doc.setFontSize(12);
    doc.setTextColor(40, 40, 40);
    doc.text('SUMAR GENERAL', 20, yPosition);
    yPosition += 10;

    doc.setFontSize(10);
    doc.setTextColor(60, 60, 60);
    doc.text(`Total plƒÉ»õi √Ænregistrate: ${filteredPayments.length}`, 20, yPosition);
    yPosition += 6;
    doc.text(`Valoare totalƒÉ: ${formatCurrency(totalAmount)}`, 20, yPosition);
    yPosition += 6;
    doc.text(`Companii active: ${Object.keys(totalByCompany).length}`, 20, yPosition);
    yPosition += 15;

    // Sumar pe companii
    doc.setFontSize(12);
    doc.setTextColor(40, 40, 40);
    doc.text('SUMAR PE COMPANII', 20, yPosition);
    yPosition += 10;

    const companyData = Object.entries(totalByCompany).map(([company, total]) => [
      company,
      formatCurrency(total)
    ]);

    autoTable(doc, {
      startY: yPosition,
      head: [['Companie', 'Total PlƒÉtit']],
      body: companyData,
      theme: 'striped',
      headStyles: { fillColor: [41, 128, 185], textColor: 255 },
      styles: { fontSize: 9, halign: 'left' },
      columnStyles: {
        1: { halign: 'right', fontStyle: 'bold' }
      }
    });

    yPosition = (doc as any).lastAutoTable.finalY + 20;

    // Verificare dacƒÉ avem loc pentru urmƒÉtoarea sec»õiune
    if (yPosition > pageHeight - 60) {
      doc.addPage();
      yPosition = 25;
    }

    // Detaliatul plƒÉ»õilor pe sƒÉptƒÉm√¢ni
    doc.setFontSize(12);
    doc.setTextColor(40, 40, 40);
    doc.text('DETALIU PLƒÇ»öI PE SƒÇPTƒÇM√ÇNI', 20, yPosition);
    yPosition += 10;

    // GrupƒÉm plƒÉ»õile pentru tabel
    const paymentData = filteredPayments.map(payment => {
      const weekDetails = getWeeklyDetails(payment.weekLabel, payment.companyName);
      const remainingAmount = weekDetails ? weekDetails.totalToPay - parseFloat(payment.amount) : 0;

      return [
        formatDate(payment.paymentDate),
        payment.companyName,
        payment.weekLabel,
        formatCurrency(parseFloat(payment.amount)),
        weekDetails ? formatCurrency(weekDetails.totalToPay) : 'N/A',
        formatCurrency(Math.max(0, remainingAmount)),
        payment.description || '-'
      ];
    });

    autoTable(doc, {
      startY: yPosition,
      head: [['Data PlatƒÉ', 'Companie', 'SƒÉptƒÉm√¢na', 'Suma PlƒÉtitƒÉ', 'Total de PlƒÉtit', 'Rest de PlƒÉtit', 'Descriere']],
      body: paymentData,
      theme: 'striped',
      headStyles: { 
        fillColor: [52, 152, 219], 
        textColor: 255,
        fontSize: 8,
        fontStyle: 'bold'
      },
      styles: { 
        fontSize: 8, 
        halign: 'left',
        cellPadding: 3
      },
      columnStyles: {
        0: { halign: 'center', cellWidth: 22 },
        1: { cellWidth: 30 },
        2: { cellWidth: 25 },
        3: { halign: 'right', fontStyle: 'bold', cellWidth: 20 },
        4: { halign: 'right', cellWidth: 20 },
        5: { halign: 'right', cellWidth: 20 },
        6: { cellWidth: 35 }
      },
      alternateRowStyles: { fillColor: [245, 245, 245] },
      margin: { left: 10, right: 10 }
    });

    // Footer pe fiecare paginƒÉ
    const pageCount = doc.getNumberOfPages();
    for (let i = 1; i <= pageCount; i++) {
      doc.setPage(i);

      // Linie footer
      doc.setLineWidth(0.3);
      doc.setDrawColor(200, 200, 200);
      doc.line(20, pageHeight - 20, 190, pageHeight - 20);

      // Text footer
      doc.setFontSize(8);
      doc.setTextColor(100, 100, 100);
      doc.text('Sistema Transport - Management PlƒÉ»õi', 20, pageHeight - 12);
      doc.text(`Pagina ${i} din ${pageCount}`, 190 - 30, pageHeight - 12);
    }

    // Salvare PDF
    doc.save(fixRomanianText(`istoric_plati_${new Date().toISOString().split('T')[0]}.pdf`));

    toast({
      title: "PDF generat cu succes",
      description: `Istoricul plƒÉ»õilor a fost salvat ca PDF (${filteredPayments.length} plƒÉ»õi incluse)`,
    });
  };

  if (isLoading) {
    return (
      <div className="h-screen flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" aria-label="Loading"/>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h1 className="text-3xl font-bold">Istoric PlƒÉ»õi</h1>
          <p className="text-muted-foreground">Toate plƒÉ»õile √Ænregistrate √Æn sistem</p>
        </div>
        <div className="flex gap-2">
          <Button onClick={generatePDF} className="flex items-center gap-2 bg-red-600 hover:bg-red-700">
            <FileText className="h-4 w-4" />
            Export PDF
          </Button>
          <Button onClick={exportToCSV} variant="outline" className="flex items-center gap-2">
            <Download className="h-4 w-4" />
            Export CSV
          </Button>
        </div>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Total PlƒÉ»õi</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{filteredPayments.length}</div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Valoare TotalƒÉ</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-green-600">{formatCurrency(totalAmount)}</div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Companii Active</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{Object.keys(totalByCompany).length}</div>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Filter className="h-5 w-5" />
            Filtrare
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div>
              <Label htmlFor="search">CƒÉutare</Label>
              <div className="relative">
                <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                <Input
                  id="search"
                  placeholder="CƒÉutare..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>
            </div>
            <div>
              <Label htmlFor="company">Companie</Label>
              <Select value={selectedCompany} onValueChange={setSelectedCompany}>
                <SelectTrigger>
                  <SelectValue placeholder="Toate companiile" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Toate companiile</SelectItem>
                  {uniqueCompanies.map(company => (
                    <SelectItem key={company} value={company}>{company}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="week">SƒÉptƒÉm√¢na</Label>
              <Select value={selectedWeek} onValueChange={setSelectedWeek}>
                <SelectTrigger>
                  <SelectValue placeholder="Toate sƒÉptƒÉm√¢nile" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Toate sƒÉptƒÉm√¢nile</SelectItem>
                  {uniqueWeeks.map(week => (
                    <SelectItem key={week} value={week}>{week}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="flex items-end">
              <Button 
                onClick={() => {
                  setSearchTerm("");
                  setSelectedCompany("all");
