          <div class="container">
            <div class="header">
              <h1>üí∞ PlatƒÉ ConfirmatƒÉ</h1>
              <p style="margin: 10px 0 0 0; opacity: 0.9;">Transport Pro - Sistem Management</p>
            </div>

            <div class="content">
              <h2>Stimate ${emailData.companyName},</h2>

              <p>VƒÉ confirmƒÉm cƒÉ plata dumneavoastrƒÉ a fost √ÆnregistratƒÉ cu succes √Æn sistemul nostru!</p>

              <div class="payment-details">
                <h3>üí≥ Detalii PlatƒÉ</h3>
                <p><strong>Suma plƒÉtitƒÉ:</strong> <span class="amount">‚Ç¨${emailData.paymentData.amount}</span></p>
                <p><strong>Data plƒÉ»õii:</strong> ${new Date(emailData.paymentData.paymentDate).toLocaleDateString('ro-RO')}</p>
                <p><strong>Perioada:</strong> ${emailData.paymentData.weekLabel}</p>
                ${emailData.paymentData.notes ? `<p><strong>Observa»õii:</strong> ${emailData.paymentData.notes}</p>` : ''}
              </div>

              ${balancesSummary}

              <div style="background: #fff3cd; padding: 15px; border-radius: 8px; margin: 20px 0; border: 1px solid #ffeaa7;">
                <p style="margin: 0; color: #856404;">üìû <strong>Pentru √ÆntrebƒÉri:</strong> Nu mai este nevoie sƒÉ ne contacta»õi pentru a afla soldul - informa»õiile sunt actualizate √Æn timp real mai sus!</p>
              </div>

              <p>Mul»õumim pentru colaborarea continuƒÉ »ôi pentru promptitudinea la plƒÉ»õi!</p>
            </div>

            <div class="footer">
              <p>üöõ Transport Pro - Sistem de Management Logistic</p>
              <p>Email generat automat pe ${new Date().toLocaleDateString('ro-RO')} la ${new Date().toLocaleTimeString('ro-RO')}</p>
              <p style="margin-top: 10px; font-style: italic;">Acest email este trimis automat c√¢nd o platƒÉ este √ÆnregistratƒÉ √Æn sistem</p>
            </div>
          </div>
        </body>
      </html>
    `;

    const emailPayload: EmailData = {
      to: emailData.to,
      subject,
      html
    };

    // Try sending via multiple services
    console.log(`üìß Sending payment notification to ${emailData.to} for ${emailData.companyName}`);

    // Try Brevo SMTP first (most reliable)
    if (await FreeEmailService.sendViaBrevo(emailPayload)) {
      return true;
    }

    // Fallback to Ethereal for preview
    if (await FreeEmailService.sendViaEthereal(emailPayload)) {
      return true;
    }

    // Fallback to Gmail if configured
    if (await FreeEmailService.sendViaGmail(emailPayload)) {
      return true;
    }

    // Finally try MailerSend
    if (await FreeEmailService.sendViaMailerSend(emailPayload)) {
      return true;
    }

    console.error('‚ùå Failed to send payment notification via all email services');
    return false;
  }
}

server/mailerSendAPI.ts.content:
import type { EmailData } from './emailService';

export class MailerSendAPI {
  private static apiKey = process.env.MAILERSEND_API_KEY;
  private static baseUrl = 'https://api.mailersend.com/v1';

  static async sendEmail(emailData: EmailData): Promise<boolean> {
    try {
      if (!this.apiKey) {
        console.log('üé≠ DEMO MODE: MailerSend API key not configured');
        return false;
      }

      const payload = {
        from: {
          email: 'test@trial-yzkq340d7nl4d796.mlsender.net',
          name: 'Transport Pro'
        },
        to: [
          {
            email: emailData.to
          }
        ],
        subject: emailData.subject,
        html: emailData.html,
        attachments: emailData.attachments?.map(att => ({
          content: att.content,
          filename: att.filename,
          type: att.type,
          disposition: att.disposition
        })) || []
      };

      const response = await fetch(`${this.baseUrl}/email`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(payload)
      });

      if (response.ok) {
        console.log('‚úÖ Email sent successfully via MailerSend API');
        return true;
      } else {
        const error = await response.text();
        console.error('‚ùå MailerSend API error:', error);
        return false;
      }
    } catch (error) {
      console.error('‚ùå MailerSend API error:', error);
      return false;
    }
  }
}

client/src/pages/analytics.tsx.content:
import { useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import AnalyticsDashboard from "@/components/dashboard/AnalyticsDashboard";

export default function Analytics() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  if (isLoading) {
    return (
      <div className="h-screen flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  if (!isAuthenticated) {
    return null;
  }

  return (
    <div className="min-h-screen bg-background">
      <div className="container mx-auto p-6">
        <AnalyticsDashboard />
      </div>
    </div>
  );
}

