import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { Building, LogIn, ArrowRight } from "lucide-react";
import { motion } from "framer-motion";
import { useLocation } from "wouter";

interface Tenant {
  id: number;
  name: string;
  description?: string;
  status: string;
  companyName?: string;
  subscriptionPlan: string;
}

export default function TenantLogin() {
  const [selectedTenantId, setSelectedTenantId] = useState<string>("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const { toast } = useToast();
  const [, setLocation] = useLocation();

  // Fetch available tenants
  const { data: tenants = [], isLoading: tenantsLoading } = useQuery<Tenant[]>({
    queryKey: ['/api/admin/tenants'],
  });

  const activeTenants = tenants.filter(t => t.status === 'active');

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!selectedTenantId || !username || !password) {
      toast({
        title: "‚ùå Date incomplete",
        description: "Selecta»õi tenant-ul, username-ul »ôi parola.",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);

    try {
      const response = await fetch(`/api/tenant/${selectedTenantId}/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
        credentials: 'include'
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error || 'Login failed');
      }

      // Store selected tenant in session storage
      sessionStorage.setItem('selectedTenantId', selectedTenantId);
      sessionStorage.setItem('tenantUser', JSON.stringify(result.user));

      // Redirect to tenant-specific URL
      setLocation(`/tenant/${selectedTenantId}/dashboard`);

      toast({
        title: "‚úÖ Conectare reu»ôitƒÉ!",
        description: `Bun venit, ${result.user.username}! Tenant: ${tenants.find(t => t.id.toString() === selectedTenantId)?.name}`,
      });

    } catch (error: any) {
      toast({
        title: "‚ùå Eroare la conectare",
        description: error.message || "Username sau parolƒÉ incorectƒÉ.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const getStatusBadge = (status: string) => {
    const variants = {
      active: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300",
      inactive: "bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-300",
      suspended: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300"
    };

    return variants[status as keyof typeof variants] || variants.inactive;
  };

  if (tenantsLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-indigo-50 dark:from-gray-900 dark:via-gray-800 dark:to-blue-900 flex items-center justify-center p-6">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="w-full max-w-md"
      >
        <Card className="shadow-2xl border-0 backdrop-blur-sm bg-white/80 dark:bg-gray-900/80">
          <CardHeader className="text-center pb-8">
            <motion.div
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ delay: 0.2, type: "spring", stiffness: 200 }}
              className="w-16 h-16 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-full flex items-center justify-center mx-auto mb-4"
            >
              <Building className="w-8 h-8 text-white" />
            </motion.div>

            <CardTitle className="text-2xl font-bold gradient-text">
              üè¢ Login Multi-Tenant
            </CardTitle>
            <p className="text-gray-600 dark:text-gray-300 mt-2">
              SelecteazƒÉ tenant-ul »ôi conecteazƒÉ-te la sistemul tƒÉu
            </p>
          </CardHeader>

          <CardContent className="space-y-6">
            <form onSubmit={handleLogin} className="space-y-4">
              {/* Tenant Selection */}
              <div className="space-y-2">
                <Label htmlFor="tenant">SelecteazƒÉ Tenant *</Label>
                <Select value={selectedTenantId} onValueChange={setSelectedTenantId}>
                  <SelectTrigger className="w-full">
                    <SelectValue placeholder="Alege tenant-ul tƒÉu..." />
                  </SelectTrigger>
                  <SelectContent>
                    {activeTenants.map((tenant) => (
                      <SelectItem key={tenant.id} value={tenant.id.toString()}>
                        <div className="flex items-center justify-between w-full">
                          <div>
                            <span className="font-medium">#{tenant.id} {tenant.name}</span>
                            {tenant.companyName && (
                              <div className="text-xs text-gray-500">{tenant.companyName}</div>
                            )}
                          </div>
                          <Badge className={getStatusBadge(tenant.status)}>
                            {tenant.status}
                          </Badge>
                        </div>
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>

                {activeTenants.length === 0 && (
                  <p className="text-sm text-red-500">
                    ‚ö†Ô∏è Nu existƒÉ tenan»õi activi disponibili
                  </p>
                )}
              </div>

              {/* Username */}
              <div className="space-y-2">
                <Label htmlFor="username">Username *</Label>
                <Input
                  id="username"
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Introduce»õi username-ul"
                  required
                />
              </div>

              {/* Password */}
              <div className="space-y-2">
                <Label htmlFor="password">Parola *</Label>
                <Input
                  id="password"
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Introduce»õi parola"
                  required
                />
              </div>

              {/* Login Button */}
              <Button
                type="submit"
                disabled={isLoading || !selectedTenantId || !username || !password}
                className="w-full bg-gradient-to-r from-blue-500 to-indigo-600 hover:from-blue-600 hover:to-indigo-700 text-white"
              >
                {isLoading ? (
                  <div className="flex items-center">
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Se conecteazƒÉ...
                  </div>
                ) : (
                  <div className="flex items-center">
                    <LogIn className="w-4 h-4 mr-2" />
                    ConecteazƒÉ-te
                    <ArrowRight className="w-4 h-4 ml-2" />
                  </div>
                )}
              </Button>
            </form>

            {/* Info Section */}
            <div className="border-t pt-4 space-y-3">
              <div className="text-center text-sm text-gray-600 dark:text-gray-400">
                <p>üîí <strong>Login securizat multi-tenant</strong></p>
                <p className="text-xs mt-1">
                  Fiecare tenant are acces doar la datele sale private
                </p>
              </div>

              {selectedTenantId && (
                <motion.div
                  initial={{ opacity: 0, height: 0 }}
                  animate={{ opacity: 1, height: "auto" }}
                  className="bg-blue-50 dark:bg-blue-900/30 p-3 rounded-lg"
                >
                  <div className="text-sm">
                    <p className="font-medium text-blue-800 dark:text-blue-300">
                      üìã Tenant selectat:
                    </p>
                    {(() => {
                      const tenant = tenants.find(t => t.id.toString() === selectedTenantId);
                      return tenant ? (
                        <div className="mt-1 text-blue-700 dark:text-blue-400">
                          <p><strong>#{tenant.id}</strong> - {tenant.name}</p>
                          {tenant.companyName && <p>üè¢ {tenant.companyName}</p>}
                          <p>üì¶ Plan: {tenant.subscriptionPlan}</p>
                        </div>
                      ) : null;
                    })()}
                  </div>
                </motion.div>
              )}
            </div>

            {/* Back to main login */}
            <div className="text-center">
              <Button
                variant="ghost"
                onClick={() => setLocation('/login')}
                className="text-sm text-gray-500 hover:text-gray-700"
              >
                ‚Üê √énapoi la login principal
              </Button>
            </div>
          </CardContent>
        </Card>
      </motion.div>
    </div>
  );
}

client/src/pages/tenant-management.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { Plus, Edit, Trash2, Building, Users, Activity } from "lucide-react";
import { motion } from "framer-motion";
import { apiRequest } from "@/lib/queryClient";

interface Tenant {
  id: number;
  name: string;
  description?: string;
  status: string;
  contactEmail?: string;
  contactPhone?: string;
  companyName?: string;
  subscriptionPlan: string;
  createdAt: string;
  updatedAt: string;
}

interface TenantStats {
  totalTenants: number;
  activeTenants: number;
  inactiveTenants: number;
  tenants: Tenant[];
}

export default function TenantManagement() {
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [editingTenant, setEditingTenant] = useState<Tenant | null>(null);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    status: "active",
    contactEmail: "",
    contactPhone: "",
    companyName: "",
    subscriptionPlan: "professional"
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch tenant statistics
  const { data: stats, isLoading } = useQuery<TenantStats>({
    queryKey: ['/api/admin/tenant-stats'],
  });

  // Fetch all tenants
  const { data: tenants = [] } = useQuery<Tenant[]>({
    queryKey: ['/api/admin/tenants'],
  });

  // Create tenant mutation
  const createTenantMutation = useMutation({
    mutationFn: (data: any) => apiRequest('/api/admin/tenants', {
      method: 'POST',
      body: JSON.stringify(data),
    }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/tenants'] });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/tenant-stats'] });
      setShowCreateDialog(false);
      resetForm();
      toast({
        title: "‚úÖ Tenant creat cu succes!",
        description: `Tenant-ul "${formData.name}" a fost adƒÉugat √Æn sistem.`,
      });
    },
    onError: () => {
      toast({
        title: "‚ùå Eroare la crearea tenant-ului",
        description: "Nu s-a putut crea tenant-ul. √éncerca»õi din nou.",
        variant: "destructive",
      });
    },
  });

  // Update tenant mutation
  const updateTenantMutation = useMutation({
    mutationFn: ({ id, data }: { id: number; data: any }) => 
      apiRequest(`/api/admin/tenants/${id}`, {
        method: 'PUT',
        body: JSON.stringify(data),
      }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/tenants'] });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/tenant-stats'] });
      setEditingTenant(null);
      resetForm();
      toast({
        title: "‚úÖ Tenant actualizat!",
        description: "Datele tenant-ului au fost actualizate cu succes.",
      });
    },
  });

  // Delete tenant mutation
  const deleteTenantMutation = useMutation({
    mutationFn: (id: number) => apiRequest(`/api/admin/tenants/${id}`, {
      method: 'DELETE',
    }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/tenants'] });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/tenant-stats'] });
      toast({
        title: "‚úÖ Tenant »ôters!",
        description: "Tenant-ul a fost eliminat din sistem.",
      });
    },
    onError: () => {
      toast({
        title: "‚ùå Eroare la »ôtergerea tenant-ului",
        description: "Nu se poate »ôterge tenant-ul principal (ID: 1).",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setFormData({
      name: "",
      description: "",
      status: "active",
      contactEmail: "",
      contactPhone: "",
      companyName: "",
      subscriptionPlan: "professional"
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (editingTenant) {
      updateTenantMutation.mutate({ id: editingTenant.id, data: formData });
    } else {
      createTenantMutation.mutate(formData);
    }
  };

  const startEdit = (tenant: Tenant) => {
    setEditingTenant(tenant);
    setFormData({
      name: tenant.name,
      description: tenant.description || "",
      status: tenant.status,
      contactEmail: tenant.contactEmail || "",
      contactPhone: tenant.contactPhone || "",
      companyName: tenant.companyName || "",
      subscriptionPlan: tenant.subscriptionPlan
    });
    setShowCreateDialog(true);
  };

  const handleDelete = (id: number, name: string) => {
    if (id === 1) {
      toast({
        title: "‚ùå Opera»õiune interzisƒÉ",
        description: "Nu se poate »ôterge tenant-ul principal.",
        variant: "destructive",
      });
      return;
    }

    if (window.confirm(`Sigur dori»õi sƒÉ »ôterge»õi tenant-ul "${name}"?`)) {
      deleteTenantMutation.mutate(id);
    }
  };

  const getStatusBadge = (status: string) => {
    const variants = {
      active: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300",
      inactive: "bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-300",
      suspended: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300"
    };

    return variants[status as keyof typeof variants] || variants.inactive;
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
            üè¢ Gestionare Tenan»õi
          </h1>
          <p className="text-gray-600 dark:text-gray-300">
            AdministreazƒÉ »ôi monitorizeazƒÉ to»õi tenantii din sistem
          </p>
        </div>

        <Dialog open={showCreateDialog} onOpenChange={(open) => {
          setShowCreateDialog(open);
          if (!open) {
            setEditingTenant(null);
            resetForm();
          }
        }}>
          <DialogTrigger asChild>
            <Button className="bg-blue-600 hover:bg-blue-700">
              <Plus className="mr-2 h-4 w-4" />
              Tenant Nou
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-md">
            <DialogHeader>
              <DialogTitle>
                {editingTenant ? "‚úèÔ∏è EditeazƒÉ Tenant" : "‚ûï Tenant Nou"}
              </DialogTitle>
            </DialogHeader>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <Label htmlFor="name">Nume Tenant *</Label>
                <Input
                  id="name"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  required
                />
              </div>

              <div>
                <Label htmlFor="description">Descriere</Label>
                <Textarea
                  id="description"
                  value={formData.description}
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                />
              </div>

              <div>
                <Label htmlFor="companyName">Nume Companie</Label>
                <Input
                  id="companyName"
                  value={formData.companyName}
                  onChange={(e) => setFormData({ ...formData, companyName: e.target.value })}
                />
              </div>

              <div>
                <Label htmlFor="contactEmail">Email Contact</Label>
                <Input
                  id="contactEmail"
                  type="email"
                  value={formData.contactEmail}
                  onChange={(e) => setFormData({ ...formData, contactEmail: e.target.value })}
                />
              </div>

              <div>
                <Label htmlFor="contactPhone">Telefon</Label>
                <Input
                  id="contactPhone"
                  value={formData.contactPhone}
                  onChange={(e) => setFormData({ ...formData, contactPhone: e.target.value })}
                />
              </div>

              <div>
                <Label htmlFor="status">Status</Label>
                <Select value={formData.status} onValueChange={(value) => setFormData({ ...formData, status: value })}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="active">Activ</SelectItem>
                    <SelectItem value="inactive">Inactiv</SelectItem>
                    <SelectItem value="suspended">Suspendat</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label htmlFor="subscriptionPlan">Plan Abonament</Label>
                <Select 
                  value={formData.subscriptionPlan} 
                  onValueChange={(value) => setFormData({ ...formData, subscriptionPlan: value })}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
